import React, { useEffect, useLayoutEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import './profile.css';
import ProfileHeader from '../components/profile/profile-header';
import BlobToImage from '../util/blob-to-image';
import LoadingSpinner from '../components/loader';
import Post from '../components/post/post';
import SideNav from '../components/sidebar/sidenav';
import RefreshLogin from '../util/refresh-login';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

function profile() {
    const [modal, setModal] = useState(false);

    const {userId} = useParams();
    const [page, setPage] = useState(1);
    const [posts, setPosts] = useState('');
    const [images, setImages] = useState('');
    const [selectedPostIndex, setSelectedPostIndex] = useState('');

    const [loading, setLoading] = useState(false);
    const [resultsEnd, setResultsEnd] = useState(false);

    const url = JSON.parse(localStorage.getItem('url')) || null;


    useEffect(() => {
        // Check if userId is available
        console.log('userId:', userId);
      }, [userId]);

    //Initialise posts
    useEffect(() => {        
        document.title = 'Profile - Social Dynamo';

        const fetchData = async () => {
            await getUserPosts();
        };
        fetchData();
    }, []);

    //Stop scrolling when post modal is open
    useEffect(() => {

        document.body.style.overflow = modal ? 'hidden' :  'unset';

        // Cleanup: Reset the style when the component is unmounted
        return () => {
            document.body.style.overflow = 'unset';
        };
    }, [modal]);

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    //Get user posts from api
    const getUserPosts = async () => {
        try{
            //Fetch data
            const response = await fetch(url + `/baseaggregator/userposts/${userId}/${page}`, {
                method: 'GET',
                mode: 'cors',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            //Handle different responses
            if (response.ok) {                
                if(!response.headers.get('content-type').includes('application/json')) return;

                const responseJson = await response.json();
                setPosts((prevData) => [...prevData, ...responseJson]);  
                setPage(page + 1);  

                for (const post of data) {
                    const image = await fetchImages(post.Files[0]);
                    if (image) {
                        setImages((prevImages) => [...prevImages, image]);
                    }
                }

            } else if (response.status === 401){
                RefreshLogin();
                getUserPosts();
            }else {
                displayError(response.title);
                setResultsEnd(true);
            }
        }catch(error){
            displayError("Unexpected error occurred, unable to fetch posts.");
        }
    };

    //Convert blob to url for post grid
    const fetchImages = async (image) => {
        const result = await BlobToImage({ byteArray: image });
        setImages((prevImages) => [...prevImages, result]);
    };

    //Infinite scrolling logic
    const handleScroll = () => {
        const isScrollingToBottom =
            window.innerHeight + document.documentElement.scrollTop + 1 >=
            document.documentElement.scrollHeight

        if (isScrollingToBottom && !loading && !resultsEnd) {
            setLoading(true);        
            getUserPosts();
        }
        setLoading(false);
    };

    useLayoutEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    //Set post data and open modal
    const openPost = (event, index) => {
        const selectedPost = posts[index];

        if (selectedPost) {
            setSelectedPostIndex(index);
            toggleModal();
        } else {
            console.error("Selected post is undefined.");
        }
    };

    const handleDeletePost = async(postId) =>{
        const objMap = { postId };
        const finalBody = JSON.stringify(objMap);

        try{
          //Fetch data
          const response = await fetch(url + '/posts/deletepost', {
            method: 'DELETE',
            mode: 'cors',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
          });
          const responseJson = await response.json();

          //Handle different responses
          if (response.ok) {
            setPosts(posts.filter(post => post.postId !== postId));
          } else if (response.status.valueOf(401)){
            RefreshLogin();
            handleDeletePost();
          }else if (response.status === 400){            
            displayError(responseJson.title)
          }else{
            displayError("Unexpected error occurred, please try again.");
          }
        }catch(error){
          displayError("Unexpected error occurred, please try again.");
        } 
    }

    const displayError = (message) => {
        toast.error(message);
    }

    return (
        <div className='Profile'>
            <SideNav/>
            <div>
                <ProfileHeader userId={userId}/>
            </div>
            {images.length > 0 && (
                <div className='post-grid'>                
                    {images && images.map((data, index) => {
                        return (            
                            <img className="image"    
                                key={index}                            
                                src={data && data.original ? data.original : ''}
                                alt={data && data.original === null ? 'Image not found' : ''}
                                onClick={(event) => openPost(event, index)}  
                                onDelete={handleDeletePost}                              
                            />                     
                        );
                    })}
                    {loading && <LoadingSpinner />}
                    {resultsEnd && "No More Posts"}
                </div> 
                )
            }  
            {modal && (
                <div className='profile-modal'>
                    <div onClick={toggleModal} className="profile-modal-overlay"></div>
                    <div className='profile-modal-body'>     
                        {<Post post={posts[selectedPostIndex]}/>}
                        <button className="profile-close-modal" onClick={toggleModal}>
                            <CancelOutlinedIcon className='closeIcon'/>
                        </button> 
                    </div>      
                </div>
            )}
            <div className='toast-cont'>
                <ToastContainer position='top-center' theme='light' />
            </div>
        </div>
    )
}

export default profile
