import React from 'react';
import { useUser } from '../../hooks/use-user';
import UserProfileHeader from '../user-profile-header';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

const Comment = ({ data }) => {
    const [liked, setLiked] = useState(data.hasUserLiked);
    const [likes, setLikes] = useState(data.likes);
    const { userId } = useContext(useUser);
    let debounceTimeout;

    const handleLikeClick = () => {
        setLiked(!liked);

        // Update the local state of likes
        const newLikes = liked ? likes - 1 : likes + 1;
        setLikes(newLikes);
        
        if (debounceTimeout) {
            clearTimeout(debounceTimeout);
        }
    
        debounceTimeout = setTimeout(async () => {            
            //Call API to handle liking/unliking. Delay accounts for accidental clicks 
            const CommentId = data.commentId;
            const LikeUserId = userId;
            const objMap = { CommentId, LikeUserId };
            const finalBody = JSON.stringify(objMap);

            // //Send data
            // const response = await fetch('http://20.49.168.20:80/posts/likecomment', {
            //     method: 'PUT',
            //     body: finalBody,
            //     headers: {
            //         'Content-Type': 'application/json',
            //     },
            // });
        }, 300);
    };

    return (
        <div>
        <UserProfileHeader
            profilepictureblob={data.profilePictureBlob}
            name={data.name}
            userid={data.userid}
        />
        <div className='comment-body'>{data.comment}</div>
        <div className='comment-footer'>
            <div footer-left>
                {data.postedAt} {likes} likes 
            </div>
            <div footer-right>
                <FavoriteBorderIcon 
                onClick={handleLikeClick} 
                style={{ color: liked ? 'red' : 'inherit' }} 
                />
            </div>         
        </div>
        </div>
    );    
}

export default Comment;
