import React, { useEffect, useLayoutEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import './profile.css';
import ProfileHeader from './profile-header';
import BlobToImage from '../../util/blob-to-image';
import LoadingSpinner from '../loader';
import Post from '../post/post';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';

function profile({testData}) {

    //Need a grid of 9 x 9?? Might not be since it'll be an array of posts? Check Karl Hadwen
        
    //css for post in modal - expand in order to accommodate standard size?
    
    //Might need a way to trigger scrolling function
    //Might need to extent the page to 110% length in css in order to enable scrolling

    //Can potentially remove timer from scolling logic

    //Remove test data from parenthesis



    const [modal, setModal] = useState(false);
    const [error, setError] = useState('');

    const {userId} = useParams();
    const [page, setPage] = useState(1);
    const [posts, setPosts] = useState('');
    const [images, setImages] = useState('');
    const [loadingImages, setLoadingImages] = useState(true);
    const [selectedPostIndex, setSelectedPostIndex] = useState('');

    const [loading, setLoading] = useState(false);
    const [resultsEnd, setResultsEnd] = useState(false);

    //Initialise posts
    useEffect(()=> {
        const fetchData = async () => {
            await getUserPosts();
        };
        fetchData();
    }, []);

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    //Get user posts from api
    const getUserPosts = async () => {
        // //Fetch data
        // const response = await fetch(`http://20.49.168.20:80/baseaggregator/userposts/${userId}/${page}`, {
        //     method: 'GET',
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        // });

        // //Handle different responses
        // const dataWrapper = await response.json();
        // const { statusCode, data } = dataWrapper;


        const statusCode = 200;
        const data = testData;








        

        if (statusCode === 200) {
            setPosts((prevData) => [...prevData, data]);  
            setPage(page + 1); 
            setError('');   
            
            // data.forEach(post => {
            //     const image = fetchImages(post.Files[0]);
            //     setImages((prevImages) => [...prevImages, image]);
            // });
            const imagesArray = [];

            for (const post of data) {
                const image = await fetchImages(post.Files[0]);
                imagesArray.push(image);
            }

            console.log("Number f posts: " + data.length);
            setImages((prevImages) => [...prevImages, imagesArray]);
            setLoadingImages(false);

        } else {
            setError(dataWrapper.value);
            setResultsEnd(true);
        }
    };

    //Convert blob to url for post grid
    const fetchImages = async (image) => {
        const result = await BlobToImage({ byteArray: image });
        setImages((prevImages) => [...prevImages, result]);
    };
      
    //Infinite scrolling logic
    const handleScroll = () => {
        // const isScrollingToBottom =
        //     window.innerHeight + document.documentElement.scrollTop + 1 >=
        //     document.documentElement.scrollHeight
    
        // if (isScrollingToBottom && !loading && !resultsEnd) {
        //     setLoading(true);        
        //     getUserPosts();

        //     setTimeout(() => {
        //         setLoading(false);
        //     }, 1500);
        // }
    };
    
    useLayoutEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);


    //
    //
    // Should be using postId instead of index??
    //

    //Set post data and open modal
    const openPost = (index) => {
        setSelectedPostIndex(index)
        toggleModal();
    };

return (
    <div className='Profile'>
        <div className='profile-header'>
            <ProfileHeader userId={userId}/>
        </div>
        {console.log("Images length: " + images.length)}
        {loadingImages ? (
            <LoadingSpinner /> // You can render a loading spinner or other loading indicator
        ) : (
            images.length > 0 && (
            <div className='post-grid'>

                {console.log("Images inside render method: " + images.length)}

                {error && <p className="error-message">{error}</p>}
                {images && images.map((data, index) => {
                    return (
                        // <li className={"image-grid"} onClick={() => openPost(index)}>
                            <img className="image"                                
                                src={data && data.original ? data.original : ''}
                                alt={data && data.original === null ? 'Image not found' : ''}
                            />
                        // </li>
                    );
                })}
            </div> 
            )
        )}  
        {modal && (
            <div className='modal'>
                <div onClick={toggleModal} className="overlay"></div>
                <div className='modal-body'>     
                    {<Post post={posts[selectedPostIndex]}/>}
                </div>
                {loading && <LoadingSpinner />}
                {resultsEnd && "No More Posts"}
                <button className="close-modal" onClick={toggleModal}>
                    <CancelOutlinedIcon className='closeIcon'/>
                </button>
            </div>
        )}
    </div>
  )
}

export default profile
