import { useEffect, useState} from 'react'
import { useNavigate } from 'react-router-dom';
import * as ROUTES from '../constants/routes';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';

function RefreshLogin() {
    const userId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
    const url = JSON.parse(localStorage.getItem('url')) || null;
    const [modal, setModal] = useState(false);
    const navigate = useNavigate();
    
    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    const handleRefreshToken = async () =>{
        const objMap = { userId };
        const finalBody = JSON.stringify(objMap);
        
        //Fetch data
        const response = await fetch(url + '/authentication/refresh-token', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        if (!response.ok) {      
            localStorage.clear();  
            setShowAlert(true);
        }
    };

    const handleModalClose = () => {        
        navigate(ROUTES.LOGIN);
    };

    useEffect(() => {
        const fetchData = async () => {
            await handleRefreshToken();
        };

        fetchData();
    }, []);

    const displayError = (message) => {
        toast.error(message);
      }
      
    return (
        <>        
        <div className='toast-cont'>
            <ToastContainer position='top-center' theme='light' />
        </div>
            {showAlert &&  (() => {
                setModal(true)
                return (
                    <div className='modal'>
                        <div onClick={handleModalClose} className="overlay"></div>
                        <div className='post-alert'>
                            <Alert severity="error">
                                <AlertTitle>Session Expired</AlertTitle>
                                Session has expired and token cannot be refreshed. You will be redirected to login.
                            </Alert>
                        </div>
                        <button className="close-modal" onClick={handleModalClose}>
                            <CancelOutlinedIcon className='closeIcon'/>
                        </button>
                    </div>
                );
            })()}
        </>
    );    
}

export default RefreshLogin
