import React, { useState, useEffect, useRef } from 'react';

const TestData = () => {
  const [data, setData] = useState([]);
  const [page, setPage] = useState(1);
  const [containerHeight, setContainerHeight] = useState(window.innerHeight);
  const containerRef = useRef(null);
  const pageSize = 10; // Number of data points to load per page
  const maxIterations = 20; // Maximum number of iterations to prevent infinite loop

  // Simulated function to generate test data
  const generateTestData = (numberOfPoints) => {
    const testData = [];
    for (let i = 1; i <= numberOfPoints; i++) {
      testData.push({ id: i, name: `Data Point ${i}` });
    }
    return testData;
  };

  useEffect(() => {
    const loadData = () => {
      const startIndex = (page - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const newData = generateTestData(endIndex); // Generate test data up to the endIndex
      setData((prevData) => [...prevData, ...newData]);
    };

    loadData();
  }, [page]);

  useEffect(() => {
    const checkContainerHeight = () => {
      if (containerRef.current) {
        const containerElement = containerRef.current;
        const containerOffset = containerElement.offsetTop;
        const windowHeight = window.innerHeight;
        if (containerOffset + containerElement.clientHeight > windowHeight) {
          return true; // Break the loop when container height exceeds the original window height
        }
      }
      return false;
    };

    let iterations = 0;

    const loadMoreData = () => {
      setPage((prevPage) => prevPage + 1);
      iterations++;

      // Break the loop after 20 iterations even if the container height hasn't exceeded the window height
      if (iterations >= maxIterations) {
        return;
      }

      // Check if the container height exceeds the original window height
      if (!checkContainerHeight()) {
        requestAnimationFrame(loadMoreData);
      }
    };

    loadMoreData();
  }, []);

  return (
    <div ref={containerRef}>
      <h1>Test Data</h1>
      <ul>
        {data.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default TestData;
