import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; 
import { Avatar } from "@mui/material";
import BlobToImage from '../util/blob-to-image';
import "./user-profile-header.css";

export default function UserProfileHeader({profilepictureblob, name, userid}) {
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = await BlobToImage({ byteArray: profilepictureblob });
        setImageUrl(url);
      } catch (error) {
        console.error('Error fetching image:', error);
      }
    };

    fetchData();
  }, [profilepictureblob]);

  console.log("Image url in user-profile-header" + imageUrl);

  return (
    <div className="header">
      <Link to={`/p/${userid}`} style={{ textDecoration: 'none' }}>
        <div className="header-user">
          {imageUrl === 5 ? (
            <Avatar style={{ marginRight: '10px' }} src={imageUrl} />
          ) : (
            <Avatar style={{ marginRight: '10px' }}>
              {name.charAt(0).toUpperCase()}
            </Avatar>
          )}
          {name}
        </div>
      </Link>
    </div>
  );
}
