import React, { useEffect, useState } from "react";

const PAGE_SIZE = 12;

// Function to generate mock data with random values
const generateMockData = (count) => {
  // ... (same as before) ...
};

const App = () => {
  const [coinsData, setCoinsData] = useState(null); // Set initial state to null
  const [page, setPage] = useState(1);

  useEffect(() => {
    const loadData = () => {
      const mockData = generateMockData(PAGE_SIZE * page);
      if (!coinsData) {
        setCoinsData(mockData);
      } else {
        setCoinsData((prevData) => [...prevData, ...mockData]);
      }
    };

    const handleScroll = () => {
      const windowHeight = window.innerHeight;
      const scrollY = window.scrollY || window.pageYOffset;
      const bodyHeight = document.body.offsetHeight;

      if (!coinsData) return; // Return if data is still loading

      if (scrollY + windowHeight >= bodyHeight) {
        // Load more data when the scroll position reaches the bottom of the page
        setPage((prev) => prev + 1);
      }
    };

    setTimeout(() => {
      loadData();
    }, 1500);

    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, [page, coinsData]);

  if (!coinsData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <h1>Crypto Gallery</h1>
      <div className="crypto-list">
        {coinsData.map((crypto) => (
          <div key={crypto.id} className="crypto-item">
            <h3>{crypto.name}</h3>
            <p>Symbol: {crypto.symbol}</p>
            <p>Price: ${crypto.current_price.toFixed(2)}</p>
            <p>Market Cap: ${crypto.market_cap.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
