import React from 'react';

const BlobToImage = (props) => {

  // Check if blobData is null or undefined
  if (props === null || props === undefined) {
    return null;
  }

  const { byteArray } = props;

  if (byteArray.length > 1) {
    // Handle a collection of binary blobs
    return (
      <div>
        {byteArray.map((byteData, index) => {
          const blob = new Blob([new Uint8Array(byteData)], { type: 'image/jpeg' });
          const imageUrl = URL.createObjectURL(blob);
          return (
            <div key={index}>
              <img src={imageUrl} alt={`Image ${index}`} />
            </div>
          );
        })}
      </div>
    );
  } else if (byteArray.length === 1) {
    return (
      <img src={`data:image/jpeg;base64,${btoa(String.fromCharCode(...byteArray[0]))}`} alt="Image couldn't be found" />
    );
  } else {
    return null;
  }
}

export default BlobToImage;
