    const finalBody = JSON.stringify(objMap);
    
    try{
      //Fetch data
      const response = await fetch(url + '/posts/deletepost', {
        method: 'DELETE',
        mode: 'cors',
        credentials: 'include',
        body: finalBody,
        headers: {
            'Content-Type': 'application/json',
        },
      });
      const responseJson = await response.json();

      //Handle different responses
      if (response.ok) {
        setError('');
      } else if (response.status === 401){
        RefreshLogin();
        handleDeletePost();
      }else {            
        setError(responseJson.value)
      }
    }catch(error){
      setError("Unexpected error occurred, please try again.");
    }    
}

const displayError = (message) => {
  toast.error(message);
}

  return (
    <div>
        <div className='comment-profile-header'>
            <UserHeader
                profilepictureblob={data.profilePictureBlob}
                name={data.name}
                userid={data.userid}
            />
        </div>
      <div className='comment-body'>{data.comment}</div>
      <div className='comment-footer'>
        <div className='footer-left'>
            {likes} likes {data.postedAt}  
        </div>
        <div className='footer-right'>
            <FavoriteBorderIcon 
                onClick={handleLikeClick} 
                style={{ color: liked ? 'red' : 'inherit' }} 
            />
        </div>
      </div>
      <div className='toast-cont'>
        <ToastContainer position='top-center' theme='light' />
      </div>
    </div>
  );
}

export default Comment;
