import React, {useState, useEffect} from "react";
import "./post.css";
//import "./files.css";
import CommentsPopup from "../modals/comments-popup";
import UserPopup from "../modals/user-popup";
import UserProfileHeader from "../user-profile-header";
import BlobToImage from "../../util/blob-to-image";
import ImageCarousel from "./image-carousel";
//import "react-image-gallery/styles/css/image-gallery.css";
import "./image-gallery.css";
import ImageGallery from "react-image-gallery";
import styled from 'styled-components'

import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";

export default function Post({ post }) {
  const [userLiked, setUserLiked] = useState(false);
  const [likes, setLikes] = useState(post.Likes.length > 0 ? post.Likes : 0);
  const [hasMultipleFiles, setHasMultipleFiles] = useState(false)
  
  const images = BlobToImage({ byteArray: post.Files });
  
  const userRow = {
    profilepictureblob: post.ProfilePicture,
    name: post.UsersName,
    userid: post.UserId
  }
  let debounceTimeout;








  // const CustomImageGallery = ({ images, containerStyles, ...otherProps }) => {
  //   return (
  //     <div style={containerStyles}>
  //       <ImageGallery items={images} {...otherProps} />
  //     </div>
  //   );
  // };




  //
  //Need to also define max sizes for the outer container.
  //Potentially can be done without .image-gallery-image
  //
  const ImageContainer = styled.div`
    
    .image-gallery-content,
    .image-gallery-image,
    .image-gallery-slides
    {
      width: 100%;
      max-width: 598px;
      height: 250px;
      max-height: 598px;
      object-fit: contain;
    }

    
    .image-gallery-slides{
      border: 1px solid black
    }
    `;



    // .image-gallery,
    // .image-gallery-content,
    // .image-gallery-image, 
    // .image-gallery-slides,
    // .image-gallery-slide



  useEffect(() => {
    setHasMultipleFiles(post.Files.length > 1);
  }, [post.Files.length]);

  const handleLikeClick = () => {
    setUserLiked(!userLiked);

    // Update the local state of likes
    const newLikes = userLiked ? likes - 1 : likes + 1;
    setLikes(newLikes);
    
    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }

    debounceTimeout = setTimeout(async () => {            
      const postId = post.postId;
      const likeUserId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
      const objMap = { postId, likeUserId };
      const finalBody = JSON.stringify(objMap);

      //Send data
      const response = await fetch('http://20.49.168.20:80/posts/likepost', {
        method: 'PUT',
        body: finalBody,
        headers: {
            'Content-Type': 'application/json',
        },
      });
    }, 300);
  };

  const fetchPostLikes = async () => {
    // const userIds = post.likes.map((like) => like.LikeUserId);
      
    // const objMap = { userIds };
    // const finalBody = JSON.stringify(objMap);

    // //Send data              
    // const response = await fetch('http://20.49.168.20:80/account/Profiles', {
    //   method: 'PUT',
    //   body: finalBody,
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    // });

    // const data = await response.json();

    // //Map response and update
    // const processedData = post.likes.map((item, index) => ({
    //   userId: item.LikeUserId,
    //   name: data[index].forename + ' ' + data[index].surname,
    //   profilePictureBlob: data[index].profilePictureBlob
    // }));      
    
    // if(userLiked) processedData.unshift(userRow);

    // return processedData;
    const testLikesData = [
    {
      userId: 1,
      name: 'John Doe',
      profilePictureBlob: null
    },
    {
      userId: 2,
      name: 'Jane Smith',
      profilePictureBlob: null
    },
    {
      userId: 3,
      name: 'Bob Johnson',
      profilePictureBlob: null
    }];

    return testLikesData;
  };

  
  console.log(images);


  //  //
  //Set max height and max width with !important?
  //Set width with !important?
  //  Might not need to since the div wraps both the gallery and the single image. 
  //All width should be constant. 
  //Should resize images to this width, mainaining aspect ratio, maybe in blob-to-image
  //Remember to set background

  //  //
  //Ensure image is centred. 

  //  //
  //Could try only defining width, should maintain aspect ratio of height. 
  //Will just need to set height to maximum image size. 
  //Scale everything else up to fit width

  //  //
  //Could just scale everything to fit the biggest height inside 598px? 
  //


  //
  // Could scale everything up within 50 - 100px?
  //

  // const containerStyles = {
  //   width: "598px",
  //   height: '20px !important',
  //   maxHeight: `10px !important`,
  //   border: "1px solid black",
  //   //objectFit: `contain !important`,
  // };
  
  function renderLeftNav(onClick, disabled) {
    return (
      <button
        className='image-gallery-left-nav'
        disabled={disabled}
        onClick={onClick}
        icon={<ArrowCircleLeftIcon/>}>
          <ArrowCircleLeftIcon className='LeftIcon'/>
      </button>
    )
  };

  function renderRightNav(onClick, disabled) {
    return (
      <button        
        className='image-gallery-right-nav'
        disabled={disabled}
        onClick={onClick}>
          <ArrowCircleRightIcon className='RightIcon'/>
      </button>
    )
  }
    
  console.log("Has multiple files: " + hasMultipleFiles);
  return (
    <div className="post">
      <div className="post-header">             
        <UserProfileHeader profilepictureblob={post.ProfilePicture} name={post.UsersName} userid={post.UserId}/>           
      </div>
      {/* <div className="post-image"> */}
      <div className="cont" //style={containerStyles}>
      >
        {hasMultipleFiles ? (
          //<ImageCarousel images={imageUrls}/>
          //<CustomImageGallery
          <ImageContainer>
            <ImageGallery items={images} 
          
                //style={containerStyles}
                //images={images}
                //containerStyles={containerStyles}
                //renderLeftNav={(onClick, disabled) => <CustomArrow onClick={onClick} icon={<ArrowCircleLeftIcon />} />}
                //renderRightNav={(onClick, disabled) => <CustomArrow onClick={onClick} icon={<ArrowCircleRightIcon />} />}
                renderLeftNav={renderLeftNav}
                renderRightNav={renderRightNav}
                showPlayButton={false}
                showFullscreenButton={false}
                showThumbnails={false}
                autoPlay={false}
                disableKeyDown={true}
                showBullets={true}
                disableThumbnailScroll={true}
                disableThumbnailSwipe={true}
                infinite={false}
                useBrowserFullscreen={false}
                />
            </ImageContainer>

          //</div>
        ) : (
          <img
                className="image"
                //src={imageUrls[0].props.src}
                alt={`Image not found`}
            />      
        )}
      </div>     
      <div className="post-footer">
        <div className="post-footerIcons">          
          <FavoriteBorderIcon 
              onClick={handleLikeClick} 
              style={{ color: userLiked ? 'red' : 'inherit' }} 
          />
          <CommentsPopup postId={post.PostId} commentData={post.Comments}/>                   
        </div>
        {/* <UserPopup
          title={'Likes'}
          data={fetchPostLikes}
          buttonText={<span style={{ color: 'blue' }}>{likes} likes</span>}
        /> */}
        <UserPopup
          title="Likes"
          data={fetchPostLikes}
          buttonComponent={<span style={{ color: 'blue' }}>{likes} likes</span>}
        />
      </div>        
    </div>
  );  
}
