import React, { useState } from 'react'
import './edit-profile-modal.css';
import FileUpload from '../file-upload';
import RefreshLogin from '../../util/refresh-login';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import { Alert, AlertTitle } from '@mui/material';

export default function EditProfileModal() {
    const [modal, setModal] = useState(false);
    const [error, setError] = useState('');
    const [completeMessage, setCompleteMessage] = useState('');
    const [isSuccess, setIsSuccess] = useState(true);
    const [isPublishing, setIsPublishing] = useState(false);

    const [forename, setForename] = useState('');
    const [surname, setSurname] = useState('');
    const [description, setDescription] = useState('');
    const [profilePicture, setProfilePicture] = useState('');
    const fileLimit = 1;

    const [showAlert, setShowAlert] = useState(false);

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    //Handle submit
    const handleSumbit = async () => {
        let allNull = true;
        isPublishing = true;

        if(forename !== '' || surname !== ''){
            handleUserDetailsCall();
            allNull = false;
        }

        if(description !== ''){
            handleDescriptionCall();
            allNull = false;
        }

        if(profilePicture !== ''){
            handleProfilePictureCall();
            allNull = false;
        }

        allNull ? setError("No values entered, must enter at least 1 value or upload an image") : null;

        if(isSuccess){
            setError('')
            setIsPublishing(false);
            setShowAlert();            
            toggleModal();
        } else{
            setIsPublishing(false);
            setError(completeMessage); 
        }
    }

    //Handle update user details
    const handleUserDetailsCall = async () => {

        let objMap = null;

        if(forename !== '' && surname !== '')
            objMap = { forename, surname };
        else if(forename !== '')
            objMap = { forename };
        else if(surname !== '')
            objmap = { surname };
        else{
            setError("Failed to update user detaisl");
            return;
        }
        const finalBody = JSON.stringify(objMap);
        
        //Fetch data
        const response = await fetch('http://20.49.168.20:80/account/updateuserdetails', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setForename('');
            setSurname('');
        } else if (statusCode === 401){
            RefreshLogin();
            handleUserDetailsCall();
        }else {            
            setIsSuccess(false);
            setCompleteMessage((prevValue) => 
                [...prevValue, dataWrapper.value]);
        }
    }

    //Handle update profile description 
    const handleDescriptionCall = async () => {
        const objMap = { description };
        const finalBody = JSON.stringify(objMap);
        
        //Fetch data
        const response = await fetch('http://20.49.168.20:80/account/updateprofiledescription', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setDescription('');
        } else if (statusCode === 401){
            RefreshLogin();
            handleDescriptionCall();
        }else {            
            setIsSuccess(false);
            setCompleteMessage((prevValue) => 
                [...prevValue, dataWrapper.value]);
        }
    }

    //Handle update profile picture
    const handleProfilePictureCall = async () => {
        const objMap = { profilePicture };
        const finalBody = JSON.stringify(objMap);
        
        //Fetch data
        const response = await fetch('http://20.49.168.20:80/account/uploadprofilepicture', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setProfilePicture('');
        } else if (statusCode === 401){
            RefreshLogin();
            handleProfilePictureCall();
        }else {            
            setIsSuccess(false);
            setCompleteMessage((prevValue) => 
                [...prevValue, dataWrapper.value]);
        }
    }

    //Handle file change in FileUpload component
    const handleFilesChange = (file) => {        
        setProfilePicture(() => {
            const picture = [...file];
                        
            return picture;
        });
    };

    //Handle cancel post
    const handleCancelPost = () => {
        setForename('');
        setSurname('');
        setDescription('');
        setProfilePicture('');
        setError('');
        toggleModal();
    }

  return (
    <>
        <button className="edit-icon" onClick={toggleModal}>
            <span>Edit</span>
        </button>
        
        {showAlert && (
            <div className='post-alert'>
                <Alert severity="success">
                    <AlertTitle>Post Published</AlertTitle>
                    Post published successfully!
                </Alert>
            </div>
        )}

        {modal && (
            <div className='modal'>
                <div onClick={toggleModal} className="overlay"></div>
                <div className='modal-body'>
                    <h1>Edit Details</h1>
                    <div className='user-details'>
                        <input
                            type="text"
                            placeholder="Forename"
                            className='user-input'
                            onChange={({ target }) => setForename(target.value)}
                            value={forename}
                        />
                        <input
                            type="text"
                            placeholder="Surname"
                            className="user-input"
                            onChange={({ target }) => setSurname(target.value)}
                            value={surname}
                        />
                        <textarea
                            placeholder="Profile description"
                            className="description-textarea"
                            rows='2'
                            onChange={({ target }) => setDescription(target.value)}
                            value={description}
                        />
                    </div>
                
                    <div className="modal-upload">
                        <FileUpload fileLimit={fileLimit} onFilesChange={handleFilesChange} setError={setError} />
                    </div>
                    <div className='modal-footer'>
                        {error && <p className="error-message">{error}</p>}
                        <button className="cancel-post" onClick={handleCancelPost} disabled={isPublishing}>
                            Cancel
                        </button>
                        <button className="publish-post" onClick={handleSumbit} disabled={isPublishing}>
                            Confirm
                        </button>                        
                    </div>
                        
                    <button className="close-modal" onClick={toggleModal} disabled={isPublishing}>
                        <CancelOutlinedIcon className='closeIcon'/>
                    </button>
                </div>  
            </div>
        )}
    </>
  )
}
