import React, { useState } from 'react';
import Post from '../components/post/post';
import "./search.css"
import SearchIcon from '@mui/icons-material/Search';

//Design
//
//Search bar at top, search button (also allow Enter to search)
//Results below, line to split sections, maybe titles??
//
//


const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState({ users: [], posts: [] });

  const handleSearch = async () => {
    
    await searchApiCall();
    
    //Clear search bar
    setSearchTerm('');
  };

  const searchApiCall = async () => {
    try {
      //Send data              
      const response = await fetch(`http://20.49.168.20:80/search/${searchTerm}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      // Assuming data is an array of mixed user and post data
      const users = [];
      const posts = [];

      data.forEach(item => {
        if ('userId' in item) {
          // It's user data
          users.push(item);
        } else if ('postId' in item) {
          // It's post data
          posts.push(item);
        }
        // Handle other cases if needed
      });

      setSearchResults({
        users,
        posts
      });
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className='search'>
      <h1>Search</h1>
      <div className='search-top'>
        <input
          className='search-bar'
          type="text"
          placeholder="Search for a user or hashtag"
          value={searchTerm}
          onChange={({target}) => setSearchTerm(target.value)}
          onKeyDown={handleKeyPress}
        />
        <SearchIcon className={"search-icon"} onClick={handleSearch}/>
      </div>
      <div className='user-results'>
        {/* Map data array to UserProfileHeader components */}
        {searchResults.users.map((userData, index) => {
          return (
            <div key={index}>
              <UserProfileHeader                            
                profilepictureblob={userData.profilePictureBlob}
                name={userData.forename + ' ' + userData.surname}
                userid={userData.userid}
              />
            </div>
          );
        })}
      </div>
      <div className='post-results'>
        {searchResults.posts.map((postData, index) => {
          return (
            <div key={index}>
              <Post                            
                postId={postData.PostId}
                userId={postData.UserId}
                usersName={postData.UsersName}
                profilePicture={postData.ProfilePicture}
                hashtag={postData.Hashtag}
                caption={postData.Caption}
                postedAt={postData.PostedAt}
                mediaItemIds={postData.MediaItemIds}
                files={postData.Files}
                likes={postData.Likes}
                comments={postData.Comments}
              />
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Search;
