import { createContext, useState, useContext } from 'react'
import PropTypes from 'prop-types';

const UserContext = createContext<UserProvider.propTypes | null>(null);

export function useUser() {
    const context = useContext(UserContext);
    if (!context) {
      throw new Error('useUser must be used within a UserProvider');
    }
    return context;
}

export default function UserProvider({ children }) {
    const [values, setValues] = useState<UserProvider.propTypes>({
      userId: null,
      name: null,
      profileDescription: null,
      numberFollowers: null,
      profilePicture: null,
    });
          
    const updateValue = (key, newValue) => {
        setValues((prevValues) => ({
            ...prevValues,
            [key]: newValue,
        }));
    };

    const setAllValues = (newValues) => {
        console.log('Setting all values:', newValues);
        setValues(prevValues => ({ ...prevValues, ...newValues }));
        console.log(newValues.userId);
        console.log(values.userId);
        //
        console.log(values.userId);
    };

    UserProvider.propTypes = {
        userId: PropTypes.string,
        name: PropTypes.string,
        profileDescription: PropTypes.string,
        numberFollowers: PropTypes.number,
        profilePicture: any
      };

    return (
        <UserContext.Provider value={{ ...values, updateValue, setAllValues }}>
          {children}
        </UserContext.Provider>
    );          
}
