import React, { useState, useEffect, useLayoutEffect, useRef } from 'react';
import LoadingSpinner from '../components/loadingspinner';
import ReactLoader from '../components/loader';

const PAGE_NUMBER = 1;
const MaxSeed = 100;
var currPage = 1;

const TestData = () => {
  const [data, setData] = useState([]);
  const [containerHeight, setContainerHeight] = useState(0);
  const containerRef = useRef();
  const pageSize = 10; // Number of data points to load per page
  const maxIterations = 20; // Maximum number of iterations to prevent infinite loop
  const originalWindowHeight = window.innerHeight;
  const [page, setPage] = useState(PAGE_NUMBER);
  const [loading, setLoading] = useState(false);

  // Simulated function to generate test data
  const generateTestData = (numberOfPoints) => {
    const testData = [];
    for (let i = 1; i <= numberOfPoints; i++) {
      testData.push({ id: i, name: `Data Point ${i}` });
    }

    return testData;
  };

  useLayoutEffect(() => {
    // Measure the container height and update the state
    setTimeout(() => {
      if (containerRef.current) {
        setContainerHeight(containerRef.current.clientHeight);
      }
    }, 0);
  }, []);

  useLayoutEffect(() => {
    let iterations = 0;

    const initialDataIteration = () => {
      if (containerRef.current.clientHeight > originalWindowHeight) {
        console.log('Broken by height');
        return; // Break when the container height exceeds the original window height
      }

      if (iterations >= maxIterations) {
        console.log('Broken due to max iterations');
        return; // Break after 5 iterations (maxIterations)
      }

      addDataToPage();

      console.log(`Iteration: ${iterations}`);
      iterations++;

      requestAnimationFrame(initialDataIteration); // Continue to the next iteration
    };

    requestAnimationFrame(initialDataIteration); // Start the loop
  }, [containerHeight]);

  //Function to add data
  const addDataToPage = () => {
    let testData = [];
    console.log("In add data method");
    ++currPage;
    let startIndex = (currPage - 1) * 10
    console.log(`Start index: ${startIndex}, maxSeed: ${MaxSeed}`);

    for (let i = startIndex; i < startIndex + pageSize; i++) {        
      testData.push({ id: i, name: `Data Point ${i}` });
    }

    currPage++;    
    setData((prevData) => [...prevData, ...testData]);
    setLoading(false);
  }

  //Scroll logic
  useEffect(() => {
    setTimeout(async () => {
      addDataToPage()
    }, 1500);
}, [page]);

useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
}, []);

const handleScroll = async () => {
    if (
        window.innerHeight + document.documentElement.scrollTop + 1 >=
        document.documentElement.scrollHeight
    ) {
        setLoading(true);
        console.log(`Should be loading, loading: ${loading}`)
        setPage((prev) => prev + 1);
    }
};

  return (
    <div ref={containerRef}>
      <ul>
        {data.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
      {console.log("Should display spinner")}
      {console.log(`loading: ${loading}`)}
      {loading && <LoadingSpinner />}
    </div>
  );
};

export default TestData;
