// CreatePostModal.js

import React, { useState } from 'react';
import './create-post-modal.css';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import { Alert, AlertTitle } from '@mui/material';
import FileUpload from '../fileupload/file-upload';

export default function CreatePostModal() {
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [error, setError] = useState('');
  const [isPublishing, setIsPublishing] = useState(false);
  const fileLimit = 4;
  const [showAlert, setShowAlert] = useState(false);

  const handleFilesChange = (newFiles) => {
    setUploadedFiles(newFiles);
  };

  const handlePublishPost = async () => {
    if (uploadedFiles.length <= 0) {
      setError('Must have files added');
      return;
    }

    setIsPublishing(true);

    const authorId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
    const formFiles = new FormData();

    uploadedFiles.forEach((file) => {
      formFiles.append('files', file);
    });

    const objMap = { authorId, formFiles };
    const finalBody = JSON.stringify(objMap);

    try {
      const response = await fetch('http://20.49.168.20:80/baseaggregate/post', {
        method: 'PUT',
        body: finalBody,
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const dataWrapper = await response.json();

      if (response.ok) {
        setUploadedFiles([]);
        setError('');
        setShowAlert(true);
        setTimeout(() => {
          setShowAlert(false);
        }, 5000);
      } else {
        setError(dataWrapper.value);
      }
    } catch (error) {
      console.error('Error publishing post:', error);
      setError('An error occurred while publishing the post');
    } finally {
      setIsPublishing(false);
    }
  };

  // Other functions remain the same

  //Render
    return( 
        <>
        <div onClick={toggleModal} className="create-icon">
            <AddCircleOutlineIcon />
            <span>Create</span>
        </div>

        {showAlert && (
            <div className='post-alert'>
                <Alert severity="success">
                    <AlertTitle>Post Published</AlertTitle>
                    Post published successfully!
                </Alert>
            </div>
        )}

        {modal && (
            <div className='modal'>
                <div onClick={toggleModal} className="overlay"></div>
                <div className='modal-body'>
                    <h1>Create Post</h1>
                    <div className='post-details'>
                        <textarea
                            placeholder="Post caption or description"
                            className='caption-textarea'
                            rows='3'
                            onChange={({ target }) => setCaption(target.value)}
                            value={caption}
                        />
                        <input
                            type="text"
                            placeholder="Hashtag"
                            className="hashtag-input"
                            pattern='/#[a-z0-9]+/'
                            onChange={({ target }) => setHashtag(target.value.replace(/\s/g, ''))}
                            value={hashtag}
                        />
                    </div>
                    <div className="modal-upload">
                        <FileUpload fileLimit={fileLimit} onFilesChange={handleFilesChange} setError={setError} />
                    </div>
                    <div className='modal-footer'>
                        {error && <p className="error-message">{error}</p>}
                        <button className="cancel-post" onClick={handleCancelPost} disabled={isPublishing}>
                            Cancel
                        </button>
                        <button className="publish-post" onClick={handlePublishPost} disabled={isPublishing}>
                            Post
                        </button>                        
                    </div>
                        
                    <button className="close-modal" onClick={toggleModal} disabled={isPublishing}>
                        <CancelOutlinedIcon className='closeIcon'/>
                    </button>
                </div>                
            </div>
        )}
    </>)    
}
