import React, {useState, useLayoutEffect} from 'react';
import './profile-header.css';
import BlobToImage from '../../util/blob-to-image';
import UserPopup from '../modals/user-popup';
import EditProfileModal from '../modals/edit-profile-modal';
import { Avatar } from '@mui/material';

export default function ProfileHeader({userId}) {
    const loggedInUserId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
    const isLoggedInUserProfile = userId === loggedInUserId ? true : false;
    const [imageUrl, setImageUrl] = useState('');
    const avatarStyle = {width: "16vh", height: "16vh"};

    const [userData, setUserData] = useState('');
    const [followers, setFollowers] = useState('');
    const [followingCount, setFollowingCount] = useState('');
    const [following, setFollowing] = useState('');
    const [isCurrentFollower, setIsCurrentFollower] = useState(false);

    const [error, setError] = useState('');

    useLayoutEffect(() => {
        const fetchData = async () => {            
            setUserData(userTest); 
            imageUrl = await fetchPictureFromBlob();
            setFollowers(await fetchFollowers());
            
            const followingData = setFollowing(await fetchFollowing());
            setFollowingCount(followingData.length);
            setFollowing(await fetchFollowing());
            
        
        }
        fetchData();
    }, []);

    //Call to get profile information from API
    const fetchProfileInformation = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/account/Profile/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setError('');
            setUserData(data);        
        } else {
            setError(dataWrapper.value);
        }
    }

    //Get url for profile picture
    const fetchPictureFromBlob = async () => {
        const url = await BlobToImage({ byteArray: userData.profilePicture });
        setImageUrl(url);      
    };
   
    //Call to get user follower information from API
    const fetchFollowers = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/account/followers/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setError('');
            setFollowers(data);

            //Check if logged in user already follows profile
            if(!isLoggedInUserProfile){
                data.forEach((user, index) =>{
                    if(user.userId === loggedInUserId)
                        setIsCurrentFollower(true);
                });
            }
        } else {
            setError(dataWrapper.value);
        }
    }

    //Call to get user follower information from API
    const fetchFollowing = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/account/followers/${userId}`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setError('');
            setFollowing(data);
            setFollowingCount(data.length);
        } else {
            setError(dataWrapper.value);
        }
    }

    // const followUser = async () => {
    //     const followerId = loggedInUserId;
    //     const objMap = { userId, followerId };
    //     const finalBody = JSON.stringify(objMap);
        
    //     //Fetch data
    //     const response = await fetch('http://20.49.168.20:80/account/addfollower', {
    //         method: 'PUT',
    //         body: finalBody,
    //         headers: {
    //             'Content-Type': 'application/json',
    //         },
    //     });

    //     //Handle different responses
    //     const dataWrapper = await response.json();
    //     const { statusCode, data } = dataWrapper;

    //     if (statusCode === 200) {
//            setError('');
    //         isCurrentFollower(true);
    //     } else {            
    //         setError(dataWrapper.value)
    //     }
    // }

    const followUser = () => {
        return;
    }

    return (
        <>
        {userData &&     
        <div className='profile-header-container'>          
            <div className='profile-header'>                
                <div className='profile-picture'> 
                    {imageUrl ? (
                        <Avatar src={imageUrl.original} sx={avatarStyle}/>
                    ) : (
                        <Avatar sx={avatarStyle}>                            
                            {userData && userData.forename.charAt(0).toUpperCase()}
                            
                        </Avatar>
                    )}                
                </div>                    
                <div className='follow-followers'>
                    <UserPopup
                        title="Followers"
                        data={followers}
                        buttonComponent={<span>{userData.numberOfFollowers}<br/>Followers</span>}
                    />
                </div>
                <div className='follow-following'>
                    <UserPopup
                        title="Following"
                        data={following}                        
                        buttonComponent={<span>{followingCount}<br/>Following</span>}
                    />
                </div>                    
                <div className='follow-button-container'>
                    {!isLoggedInUserProfile && !isCurrentFollower && (                            
                        <button className='follow-button' disabled={isCurrentFollower} onClick={followUser}> 
                            {!isCurrentFollower ? <span>Follow</span> : <span>Following</span>}
                        </button>                            
                    )}
                </div>        
                <div className='user-details'>                                   
                    <span className='span-name'>{userData.forename + ' ' + userData.surname}</span>
                    <br/>
                    <span className='span-userid'>{userId}</span>
                    <br/>
                </div> 
                <p className='span-description'>{userData.profileDescription}</p>
                <br/>

                {/* {isLoggedInUserProfile && (   */}
                    <div className='edit-button'>                      
                        <EditProfileModal/>
                    </div>
                {/* )}        */}
                <div className='error'>
                    {error && <p className="error-message">{error}</p>}
                </div>
            </div>    
        </div>   
                   
        }
        </>
    )
}
