import React, { useState, useEffect, useRef } from 'react';
import InfiniteScroll from '../components/infinitescroll';

const PAGE_SIZE = 5; // Number of items per page

const generateRandomData = (count) => {
  const data = [];
  for (let i = 0; i < count; i++) {
    const item = {
      id: i + 1, // Assuming IDs start from 1
      name: `Item ${i + 1}`,
      description: `Description for Item ${i + 1}`,
    };
    data.push(item);
  }
  return data;
};

const TestPage = () => {
  // Simulated in-memory paginated dataset
  const [mockData, setMockData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const containerRef = useRef(); // Ref for the scroll container
  const lastScrollTop = useRef(0); // Ref for storing last scroll position

  // Function to generate random data for a given page number
  const generatePageData = (page) => {
    setLoading(true);
    setError(null);

    // Simulate API call delay
    setTimeout(() => {
      const startIndex = (page - 1) * PAGE_SIZE;
      const endIndex = startIndex + PAGE_SIZE;
      const newData = generateRandomData(PAGE_SIZE).map((item, index) => ({
        ...item,
        id: startIndex + index + 1,
      }));

      setMockData((prevData) => [...prevData, ...newData]);
      setCurrentPage((prevPage) => prevPage + 1);
      setLoading(false);
    }, 1000); // Simulate API call delay of 1 second
  };

  // Function to handle the scroll event on the container element
  const handleScroll = () => {
    const container = containerRef.current;
    if (container) {
      const scrollTop = container.scrollTop;
      const scrollHeight = container.scrollHeight;
      const clientHeight = container.clientHeight;

      if (scrollTop > lastScrollTop.current) {
        // Scrolling down
        if (scrollTop + clientHeight >= scrollHeight - 50 && !loading) {
          // Load more data when the user has scrolled near the bottom and not already loading
          generatePageData(currentPage);
        }
      } else {
        // Scrolling up
        if (scrollTop <= clientHeight && currentPage > 1 && !loading) {
          // Load previous page when the user has scrolled near the top and not already loading
          generatePageData(currentPage - 1);
        }
      }

      lastScrollTop.current = scrollTop;
    }
  };

  // Add the scroll event listener when the component mounts
  useEffect(() => {
    const container = containerRef.current;
    if (container) {
      container.addEventListener('scroll', handleScroll);
    }

    // Remove the scroll event listener when the component unmounts
    return () => {
      if (container) {
        container.removeEventListener('scroll', handleScroll);
      }
    };
  }, [loading, currentPage]); // Add 'loading' and 'currentPage' as dependencies to prevent unnecessary event listener changes

  // Load initial data when the component mounts
  useEffect(() => {
    generatePageData(1);
  }, []);

  // Load extra data if the initial data is not enough to fill the viewport
  useEffect(() => {
    const container = containerRef.current;
    if (container) {
      const scrollHeight = container.scrollHeight;
      const clientHeight = container.clientHeight;
      if (scrollHeight < clientHeight && !loading) {
        generatePageData(currentPage + 1);
      }
    }
  }, [loading]); // Add 'loading' as a dependency to trigger when the initial data is loaded

  return (
    <div style={{ height: '100vh', overflowY: 'hidden' }}>
      <h1>Test Page</h1>
      <div ref={containerRef} style={{ height: 'calc(100vh - 30px)', overflowY: 'scroll' }}>
        {mockData.map((item) => (
          <div key={item.id}>
            <p>{item.name}</p>
            <p>{item.description}</p>
            {/* Customize the rendering and interactions for each item */}
            <button onClick={() => console.log('Item clicked:', item)}>Click Me</button>
          </div>
        ))}
      </div>
      {loading && <p>Loading...</p>}
    </div>
  );
};

export default TestPage;
