import { useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import * as ROUTES from '../constants/routes';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

function RefreshLogin() {
    const userId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
    const url = JSON.parse(localStorage.getItem('url')) || null;
    const navigate = useNavigate();
        
    const handleRefreshToken = async () =>{
        const objMap = { userId };
        const finalBody = JSON.stringify(objMap);
        
        //Fetch data
        const response = await fetch(url + '/authentication/refresh-token', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        if (!response.ok) {      
            localStorage.clear();  
            displayError("Session has expired and token cannot be refreshed. You will be redirected to login.");
        }
    };

    const handleModalClose = () => {        
        navigate(ROUTES.LOGIN);
    };

    useEffect(() => {
        const fetchData = async () => {
            await handleRefreshToken();
        };

        fetchData();
    }, []);

    const displayError = (message) => {
        toast.error(message);
    }

    return (
        <>        
        <div className='toast-cont'>
            <ToastContainer position='top-center' theme='light' />
        </div>
        </>
    );    
}

export default RefreshLogin
