import React, { useState } from 'react'
import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
import './image-carousel.css';
import './files.css';

const ImageCarousel= ({ images }) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    
    console.log("Testing width is present: " + images[0].width);

    const widths = images.map(img => Number(img.props.width));
    const heights = images.map(img => Number(img.props.height));

    const largestWidth = Math.max(...widths);
    const largestHeight = Math.max(...heights);
    
    console.log("largest width: " + largestWidth);
    console.log("largest height: " + largestHeight);

    const goToPrevious = () => {
        const isFirstImage = currentIndex === 0;
        const newIndex = isFirstImage ? images.length - 1 : currentIndex - 1;
        setCurrentIndex(newIndex);
    };

    const goToNext = () => {
        const isLastImage = currentIndex === images.length - 1;
        const newIndex = isLastImage ? 0 : currentIndex + 1;
        setCurrentIndex(newIndex);
    };
    
    const goToSlide = (slideIndex) => {
        setCurrentIndex(slideIndex);
    };

    return (
        <div className="slider">
            <div>
                <ArrowCircleLeftIcon onClick={goToPrevious} className="left-arrow"/>
                <ArrowCircleRightIcon onClick={goToNext} className="right-arrow"/>
            </div>
            <div className='post-image' style={{ maxWidth: largestWidth, maxHeight: largestHeight }}>
                <img
                    className="image"
                    src={images[currentIndex].props.src}
                    alt={`Image ${currentIndex}`}
                />
            </div>
            <div className="dots-container">
                {images.map((image, imageIndex) => (
                <div
                    className="dot"
                    key={imageIndex}
                    onClick={() => goToSlide(imageIndex)}
                >
                    ●
                </div>
                ))}
            </div>
        </div>
    )
}

export default ImageCarousel;
