import React, { useEffect, useLayoutEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import './profile.css';
import ProfileHeader from './profile-header';
import BlobToImage from '../../util/blob-to-image';
import LoadingSpinner from '../loader';
import Post from '../post/post';
import SideNav from '../sidebar/sidenav';
import RefreshLogin from '../../util/refresh-login';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';

function profile() {
    const [modal, setModal] = useState(false);
    const [error, setError] = useState('');

    const {userId} = useParams();
    const [page, setPage] = useState(1);
    const [posts, setPosts] = useState('');
    const [images, setImages] = useState('');
    const [selectedPostIndex, setSelectedPostIndex] = useState('');

    const [loading, setLoading] = useState(false);
    const [resultsEnd, setResultsEnd] = useState(false);

    //Initialise posts
    useEffect(() => {        
        document.title = 'Not Found - Social Dynamo';
          
        const fetchData = async () => {
            await getUserPosts();
        };
        fetchData();
    }, []);

    //Stop scrolling when post modal is open
    useEffect(() => {

        document.body.style.overflow = modal ? 'hidden' :  'unset';
        
        // Cleanup: Reset the style when the component is unmounted
        return () => {
            document.body.style.overflow = 'unset';
        };
    }, [modal]);

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    //Get user posts from api
    const getUserPosts = async () => {
        //Fetch data
        const response = await fetch(`http://20.49.168.20:80/baseaggregator/userposts/${userId}/${page}`, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setPosts((prevData) => [...prevData, ...data]);  
            setPage(page + 1); 
            setError('');   

            for (const post of data) {
                const image = await fetchImages(post.Files[0]);
                if (image) {
                    setImages((prevImages) => [...prevImages, image]);
                }
            }

        } else if (statusCode === 401){
            RefreshLogin();
        }else {
            setError(dataWrapper.value);
            setResultsEnd(true);
        }
    };

    //Convert blob to url for post grid
    const fetchImages = async (image) => {
        const result = await BlobToImage({ byteArray: image });
        setImages((prevImages) => [...prevImages, result]);
    };
      
    //Infinite scrolling logic
    const handleScroll = () => {
        const isScrollingToBottom =
            window.innerHeight + document.documentElement.scrollTop + 1 >=
            document.documentElement.scrollHeight
    
        if (isScrollingToBottom && !loading && !resultsEnd) {
            setLoading(true);        
            getUserPosts();
        }
        setLoading(false);
    };
    
    useLayoutEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    //Set post data and open modal
    const openPost = (event, index) => {
        const selectedPost = posts[index];

        if (selectedPost) {
            setSelectedPostIndex(index);
            toggleModal();
        } else {
            console.error("Selected post is undefined.");
        }
    };

    return (
        <div className='Profile'>
            <SideNav/>
            <div>
                <ProfileHeader userId={userId}/>
            </div>
            {error && <p className="error-message">{error}</p>}
            {images.length > 0 && (
                <div className='post-grid'>                
                    {images && images.map((data, index) => {
                        return (            
                            <img className="image"    
                                key={index}                            
                                src={data && data.original ? data.original : ''}
                                alt={data && data.original === null ? 'Image not found' : ''}
                                onClick={(event) => openPost(event, index)}                                
                            />                     
                        );
                    })}
                    {loading && <LoadingSpinner />}
                    {resultsEnd && "No More Posts"}
                </div> 
                )
            }  
            {modal && (
                <div className='profile-modal'>
                    <div onClick={toggleModal} className="profile-modal-overlay"></div>
                    <div className='profile-modal-body'>     
                        {<Post post={posts[selectedPostIndex]}/>}
                        <button className="profile-close-modal" onClick={toggleModal}>
                            <CancelOutlinedIcon className='closeIcon'/>
                        </button> 
                    </div>      
                </div>
            )}
        </div>
    )
}

export default profile
