import React, { useEffect, useState, useRef } from "react";

const PAGE_SIZE = 3;
const TOTAL_DATA_COUNT = 100;

// Function to generate mock data with random values
const generateMockData = (count) => {
  const mockData = [];
  for (let i = 0; i < count; i++) {
    mockData.push({
      id: i + 1,
      name: `Crypto ${i + 1}`,
      symbol: `CRYP${i + 1}`,
      current_price: Math.random() * 1000, // Random price between 0 and 1000
      market_cap: Math.random() * 1000000000, // Random market cap between 0 and 1 billion
    });
  }
  return mockData;
};

const App = () => {
  const [coinsData, setCoinsData] = useState([]);
  const [page, setPage] = useState(1);
  const containerRef = useRef(null);
  const [loading, setLoading] = useState(true);

  // Function to load more data while scrolling
  const loadData = () => {
    const container = containerRef.current;
    if (
      container.scrollTop + container.clientHeight >=
      container.scrollHeight - 50 // Load more data before reaching the bottom to avoid flickering
    ) {
      const mockData = generateMockData(PAGE_SIZE);
      setCoinsData((prevData) => [...prevData, ...mockData]);
      setPage((prevPage) => prevPage + 1);
    }
  };

  useEffect(() => {
    const container = containerRef.current;

    // Function to load initial data until the container height exceeds window height
    const loadInitialData = () => {
      const checkHeight = () => {
        if (container.scrollHeight <= container.clientHeight) {
          const mockData = generateMockData(PAGE_SIZE);
          setCoinsData((prevData) => [...prevData, ...mockData]);
          setPage((prevPage) => prevPage + 1);
          requestAnimationFrame(checkHeight);
        } else {
          setLoading(false); // Stop loading initial data
        }
      };
      requestAnimationFrame(checkHeight);
    };

    // Load initial data until container height exceeds window height
    loadInitialData();

    // Add scroll event listener for infinite scrolling
    const handleScroll = () => {
      if (loading) return;
      loadData();
    };

    container.addEventListener("scroll", handleScroll);

    // Cleanup on component unmount
    return () => {
      container.removeEventListener("scroll", handleScroll);
    };
  }, [loading]);

  return (
    <div className="app">
      <h1>Crypto Gallery</h1>
      <div className="crypto-list" ref={containerRef}>
        {coinsData.map((crypto) => (
          <div key={crypto.id} className="crypto-item">
            <h3>{crypto.name}</h3>
            <p>Symbol: {crypto.symbol}</p>
            <p>Price: ${crypto.current_price.toFixed(2)}</p>
            <p>Market Cap: ${crypto.market_cap.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
