import React, { useState } from 'react';
import InfiniteScroll from '../components/infinitescroll';

const PAGE_SIZE = 10; // Number of items per page

const generateRandomData = (count) => {
  const data = [];
  for (let i = 0; i < count; i++) {
    const item = {
      id: i + 1, // Assuming IDs start from 1
      name: `Item ${i + 1}`,
      description: `Description for Item ${i + 1}`,
    };
    data.push(item);
  }
  return data;
};

const TestPage = () => {
  // Simulated in-memory paginated dataset
  const [mockData, setMockData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  // Function to generate random data for a given page number
  const generatePageData = (page) => {
    setLoading(true);
    setError(null);

    // Simulate API call delay
    setTimeout(() => {
      const startIndex = (page - 1) * PAGE_SIZE;
      const endIndex = startIndex + PAGE_SIZE;
      const newData = generateRandomData(PAGE_SIZE).map((item, index) => ({
        ...item,
        id: startIndex + index + 1,
      }));

      setMockData((prevData) => [...prevData, ...newData]);
      setCurrentPage((prevPage) => prevPage + 1);
      setLoading(false);
    }, 1000); // Simulate API call delay of 1 second
  };

  // Function to simulate loading more data for pagination when scrolling
  const loadMoreData = () => {
    generatePageData(currentPage);
  };

  // Function to simulate handling clicks on individual items
  const handleItemClick = (item) => {
    // Handle the click on individual items here
    console.log('Item clicked:', item);
  };

  const renderData = (item) => {
    return (
      <div>
        <p>{item.name}</p>
        <p>{item.description}</p>
        {/* Customize the rendering and interactions for each item */}
        <button onClick={() => handleItemClick(item)}>Click Me</button>
      </div>
    );
  };

  return (
    <div>
      <h1>Test Page</h1>
      <InfiniteScroll
        data={mockData}
        loading={loading}
        error={error}
        onLoadMore={loadMoreData}
        renderData={renderData}
      />
    </div>
  );
};

export default TestPage;
