import React, { useState, useEffect } from 'react';
import LoadingSpinner from '../components/loadingspinner'; // Import your loading spinner component

const TestData = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const pageSize = 10;
  const loadDelay = 1500; // Time to simulate data fetching delay

  // Simulated function to generate test data
  const generateTestData = (numberOfPoints) => {
    const testData = [];
    for (let i = 1; i <= numberOfPoints; i++) {
      testData.push({ id: i, name: `Data Point ${i}` });
    }
    return testData;
  };

  // Function to fetch data and update state
  const fetchData = () => {
    setLoading(true); // Start loading spinner
    setTimeout(() => {
      const newData = generateTestData(pageSize);
      setData((prevData) => [...prevData, ...newData]);
      setLoading(false); // Stop loading spinner
    }, loadDelay);
  };

  // Fetch data when component mounts
  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <h1>Test Data</h1>
      <ul>
        {data.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
      {/* Display loading spinner when loading is true */}
      {loading && <LoadingSpinner />}
    </div>
  );
};

export default TestData;
