import React, { useState, useRef } from 'react'
import './create-post-modal.css';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import TaskIcon from '@mui/icons-material/Task';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';

export default function CreatePostModal(){
    const [modal, setModal] = useState(false);
    const [error, setError] = useState('');

    const [caption, setCaption] = useState('');
    const [hashtag, setHashtag] = useState('');
    const [files, setFiles] = useState([]);
    const fileLimit = 4;
    const fileInputRef = useRef(null);

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    //Handle publish post logic
    const handlePublishPost = async () => {
        
        if (files.length <= 0 && caption.trim() === '') {
            setError("Must have a caption and files added");
            return;
        }

        const authorId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
        const formFiles = new FormData();

        files.forEach((file, index) =>{
            formFiles.append(file);
        });

        const objMap = { authorId, hashtag, caption, formFiles };
        const finalBody = JSON.stringify(objMap);
    
        //Fetch data
        const response = await fetch('http://20.49.168.20:80/baseaggregate/post', {
            method: 'PUT',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode === 200) {
            setFiles([]);
            setCaption('');
            setHashtag('');
            setError("");
            toggleModal();
        } else {
            setError(dataWrapper.value);
        }
    };

    //Handle add files
    const handleAddFile = (event) => {
        if(files.length !== fileLimit){
            const file = event.target.files[0];
            setFiles((prevFiles) => [...prevFiles, file]);
            setError("");
        }else{
            setError("File limit reached");
        }

        // Clear the file input
        if (fileInputRef.current) {
            fileInputRef.current.value = null;
        }
    }

    //Handle remove files
    const handleRemoveFile = (filename) => {
        setFiles(files.filter(file => file.name !== filename));        
        setError("");
    }



    ///
    ///
    //USE WEBDEVPILLS VID - [ REACT JS ] File Upload
    ///
    ///

    //Must get and pass bearer token.

    //Allow for drag and drop
    //Sort buttons at bottom

    //Some sort of popup feedback for post created successfully.

    //Progress bar for uploading, should prevent exiting from modal!!!!!
    //Maybe just await but that could jut allow closing after despite post not being published if there's an error
    //Tutorial beginning frm OpenJavascript

    //Check caption and hashtag fail and return errors if exceeding sizes.



    //Render
    return( 
        <>
        <AddCircleOutlineIcon onClick={toggleModal}>
            <span>Create</span>
        </AddCircleOutlineIcon>

        {modal && (
            <div className='modal'>
                <div onClick={toggleModal} className="overlay"></div>
                <div className='modal-body'>
                    <h1>Create Post</h1>
                    <div className='post-details'>
                        <textarea
                            placeholder="Post caption or description"
                            className='caption-textarea'
                            rows='3'
                            onChange={({ target }) => setCaption(target.value)}
                            value={caption}
                        />
                        <input
                            type="text"
                            placeholder="Hashtag"
                            className="hashtag-input"
                            pattern='/#[a-z0-9]+/'
                            onChange={({ target }) => setHashtag(target.value.replace(/\s/g, ''))}
                            value={hashtag}
                        />
                    </div>
                    <div className='modal-upload'>
                        <div className='upload-container'>
                            <input type="file" ref={fileInputRef} accept="image/png, image/jpeg" onChange={handleAddFile}/>
                            <button>
                                <i>
                                    <AddCircleOutlineIcon/>
                                </i>
                                Select
                            </button>
                        </div>
                        <p className="main">Supported files</p>
                        <p className="info">JPG, PNG</p>
                    </div>
                    <div className='uploaded-files'>
                        {files.map((file, index) => {
                            return(
                                <li className="file-list">
                                    <TaskIcon/>
                                    <span>{file.name}</span>
                                    <div className='delete-icon'>
                                        <DeleteOutlineIcon onClick={() => handleRemoveFile(file.name)}/>   
                                    </div>                                 
                                </li>
                            );
                        })}
                    </div>
                    <div className='modal-footer'>
                        {/*error && <p className="mb-4 text-xs text-red-primary">{error}</p>*/}
                        <button className="publish-post" onClick={handlePublishPost}>
                            Post
                        </button>
                        <button className="cancel-post" onClick={toggleModal}>
                            Cancel
                        </button>
                        <button>
                            Some text!!!
                        </button>
                    </div>

                    <button className="close-modal" onClick={toggleModal}>
                        <CancelOutlinedIcon className='closeIcon'/>
                    </button>
                </div>                
            </div>
        )}
    </>)    
}
