import React from 'react'
import { Link, useNavigate } from 'react-router-dom'; 
import "./sidenav.css"
import CreatePostModal from '../modals/create-post-modal';
import * as ROUTES from '../../constants/routes';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LogoutIcon from '@mui/icons-material/Logout';
import SocialDynamoLogo from './social-dynamo-logo.png';

function SideNav() {
  const userId = JSON.parse(localStorage.getItem('userId'))?.userId || null;
  const url = JSON.parse(localStorage.getItem('url')) || null;
  const navigate = useNavigate();

  const handleLogout = async () =>{
    const objMap = { userId };
    const finalBody = JSON.stringify(objMap);
    
    try{
      //Fetch data
      const response = await fetch(url + '/authentication/logout', {
        method: 'PUT',
        mode: 'cors',
        body: finalBody,
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      //Handle different responses
      if (response.status === 200) {      
        localStorage.clear();
        navigate(ROUTES.LOGIN)
      }else{

      }
    }catch(error){
      // setError("Unexpected error occurred, please try again.");
      console.log(error);
    }
  }

  const displayError = (message) => {
    toast.error(message);
  }

  return (
    <div className='sidenav'>
      <Link to={`${ROUTES.DASHBOARD}`} style={{ textDecoration: 'none' }}>
        <img className='sidenav-logo' src={SocialDynamoLogo} alt='Image Not Found'/>
      </Link>
      
      <div className="sidenav-buttons">
        <Link to={`${ROUTES.DASHBOARD}`} style={{ textDecoration: 'none' }}>
            <div className='sidenav-button'>
                <HomeIcon/>
                <span>Home</span>
            </div>
        </Link>

        <Link to={`${ROUTES.SEARCH}`} style={{ textDecoration: 'none' }}>
            <div className='sidenav-button'>
                <SearchIcon />
                <span>Search</span>
            </div>
        </Link>

        <Link to={`/p/${userId}`} style={{ textDecoration: 'none' }}>
            <div className='sidenav-button'>
                <AccountCircleIcon/>
                <span>Profile</span>
            </div>
        </Link>

        <div className='sidenav-button'>
            <CreatePostModal/>
        </div>     

        <br/>
        <br/>
        <hr style={{width: '100%'}}/>
        
        
        <div className='sidenav-button' onClick={handleLogout}>    
            <LogoutIcon/>        
            <span className='logout-button'>Logout</span>            
        </div> 
        
      </div>
      <div className='toast-cont'>
        <ToastContainer position='top-center' theme='light' />
      </div>
    </div>    
  )
}

export default SideNav;
