import React, {useState, useEffect} from "react";
import { Link } from 'react-router-dom'; 
import "./post.css";
import CommentsPopup from "../modals/comments-popup";
import UserPopup from "../modals/user-popup";
import UserHeader from "../user-header";
import BlobToImage from "../../util/blob-to-image";
import RefreshLogin from "../../util/refresh-login";
import "./image-gallery.css";
import ImageGallery from "react-image-gallery";
import styled from 'styled-components'
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";

export default function Post({ post, onDelete }) {
  const loggedInUserId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
  const isLoggedInUserProfile = post.userId === loggedInUserId ? true : false;
  const url = JSON.parse(localStorage.getItem('url')) || null;
  const [userLiked, setUserLiked] = useState(false);
  const [likes, setLikes] = useState(post.Likes.length > 0 ? post.Likes : 0);
  const [hasMultipleFiles, setHasMultipleFiles] = useState(false)

  const maxWidth = window.innerWidth * 0.4;
  const maxHeight = (maxWidth / 3) * 4;
  
  const [images, setImages] = useState([]);
  const [largestImageHeight, setLargestImageHeight] = useState(maxHeight);
  const [ImageContainer, setImageContainer] =  useState(null);
  const [imageContainerStyle, setImageContainerStyle] = useState({});

  const userRow = {
    profilepictureblob: post.ProfilePicture,
    name: post.UsersName,
    userId: post.UserId
  }
  let debounceTimeout;
  
  //Initialise multiple files const & images
  useEffect(() => {
    setHasMultipleFiles(post.Files.length > 1);

    const fetchImages = async () => {
      const result = await BlobToImage({ byteArray: post.Files });
      setImages(result);
    };

    fetchImages();

  }, [post.Files.length]);

  //Separate useEffect to ensure images fetched before calculations
  useEffect(() => {
      let maxAdjustedHeight = 0;

      if (images.length > 0) {
            
          // Calculate the adjusted height for each image based on the original aspect ratio
          const adjustedHeights = images.map(image => {
          const aspectRatio = image.width / (image.height || 1);          
          const adjustedHeight = Math.min(maxHeight, parseInt(maxWidth / aspectRatio));

          return isFinite(adjustedHeight) ? adjustedHeight : 0;
        });
    
        // Filter out NaN or infinite values
        const validHeights = adjustedHeights.filter(height => isFinite(height));
    
        if (validHeights.length > 0) {
          // Find the maximum adjusted height among all valid heights
          maxAdjustedHeight = Math.max(...validHeights);
          setLargestImageHeight(`${maxAdjustedHeight}px`);
        } else {
          setLargestImageHeight(0); // Reset to 0 if there are no valid heights
        }
      } else {
        setLargestImageHeight(0); // Reset to 0 if there are no images
      }

      //Ensure correct styling
      const imgCont = styled.div`    
        .image-gallery-content,
        .image-gallery-image,
        .image-gallery-slides
        {
          width: 100%;
          aspect-ratio: 4/3;
          max-width: ${maxWidth}px !important;
          height: ${maxAdjustedHeight}px;
          max-height: ${maxHeight}px;
          object-fit: contain;
        }
        
        .image-gallery-slides{
          border: 1px solid black;
          border-radius: 5px;
        }
        `;

      //Styling if no images
      const containerStyle = images.length === 0
      ? {
          width: `${maxWidth}px`,
          height: `${maxWidth}px`,
          border: '1px solid black'
        }
      : {
          maxWidth: `${maxWidth}px`,
          width: `${maxWidth}px`,
          width: '100%',
          maxHeight: `${maxHeight}px`,
          border: '1px solid black'
      };
      setImageContainerStyle(containerStyle);      
      setImageContainer(imgCont);

  }, [images]);

  //Handle post like
  const handleLikeClick = () => {
    setUserLiked(!userLiked);

    // Update the local state of likes
    const newLikes = userLiked ? likes - 1 : likes + 1;
    setLikes(newLikes);
    
    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }

    debounceTimeout = setTimeout(async () => {            
      const postId = post.postId;
      const likeUserId = loggedInUserId;
      
      const objMap = { postId, likeUserId };
      const finalBody = JSON.stringify(objMap);

      likePost(finalBody);
    }, 300);

    
  };

  const likePost= async (body) => {
    try{
      //Send data
      const response = await fetch(url + '/posts/likepost', {
        method: 'PUT',
        mode: 'cors',
        credentials: 'include',
        body: finalBody,
        headers: {
            'Content-Type': 'application/json',
        },
      });

      if(response.status === 401){
        RefreshLogin();
        handleLikeClick();
      }

    }catch(error){
      setError("Unexpected error occurred, please try again.");
    }
  }

  //Fetch current likes from API
  const fetchPostLikes = async () => {
    const userIds = post.likes.map((like) => like.LikeUserId);
      
    const objMap = { userIds };
    const finalBody = JSON.stringify(objMap);

    try{
      //Send data              
      const response = await fetch(url + '/account/Profiles', {
        method: 'PUT',
        mode: 'cors',
        body: finalBody,
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      if(response.ok){
        //Map response and update
        const processedData = post.likes.map((item, index) => ({
          userId: item.LikeUserId,
          name: data[index].forename + ' ' + data[index].surname,
          profilePictureBlob: data[index].profilePictureBlob
        }));      
      }else if(response.status === 401){
        RefreshLogin();
        fetchPostLikes();
      }
      
      if(userLiked) processedData.unshift(userRow);
    }catch(error){
      setError("Unexpected error occurred, unable to fetch post likes.");
    }

    return processedData;
  };

  //Handle delete post
  const handleDeletePost = async () => {
    await onDelete(post.postId);
  }
  
  const ConfirmDelete = ({closeToast}) => {
    <div>
      <span> Are you sure you want to delete? </span>
      <button className='delete-yes' onClick={handleDeleteComment}>Yes</button>
      <button className='delete-no' onClick={closeToast}>No</button>
    </div>
  }

  //Custom rendering for left nav icon
  function renderLeftNav(onClick, disabled) {
    return (
      <button
        className='image-gallery-left-nav'
        disabled={disabled}
        onClick={onClick}
        style={{outline: 'none'}}>
        <ArrowCircleLeftIcon className='left-icon' fontSize="large"/>
      </button>
    )
  };

  //Custom render for right nav icon
  function renderRightNav(onClick, disabled) {
    return (
      <button        
        className='image-gallery-right-nav'
        disabled={disabled}
        onClick={onClick}
        style={{outline: 'none'}}>          
        <ArrowCircleRightIcon className='right-icon' fontSize="large"/>        
      </button>
    )
  }
    
  return (
    <div className="post">
      <div className="post-header">  
        <div className="post-header-left">         
          <UserHeader profilepictureblob={post.ProfilePicture} name={post.UsersName} userid={post.UserId}/> 
        </div>  
        <div className="post-header-right">
          <div className='delete-button-container'>
            {isLoggedInUserProfile && (  
                <div className='delete-button' onClick={handleDeletePost}>                      
                    <span>Delete</span>
                </div>
            )}    
          </div>
        </div>          
      </div>
      <div className="cont">
        {hasMultipleFiles ? (
          <ImageContainer>
            <ImageGallery items={images} 
                renderLeftNav={renderLeftNav}
                renderRightNav={renderRightNav}
                showPlayButton={false}
                showFullscreenButton={false}
                showThumbnails={false}
                autoPlay={false}
                disableKeyDown={true}
                showBullets={true}
                disableThumbnailScroll={true}
                disableThumbnailSwipe={true}
                infinite={false}
                useBrowserFullscreen={false}
                />
            </ImageContainer>
        ) : (
          <img
            className="image"
            src={images.length > 0 ? images[0].original : ''}
            alt={images.length > 0 ? '' : 'Image not found'}
            style={imageContainerStyle}
          />     
        )}
      </div>     
      <div className="post-body">
          <h3>{post.Caption}</h3>
          <Link to={`/search?searchterm=${post.Hashtag.substring(1)}`} style={{ textDecoration: 'underline', color: '#005c98'}}>
            {post.Hashtag}
          </Link>
      </div>
      <div className="post-footer">
        <div className="post-footer-icons">          
          <FavoriteBorderIcon 
              onClick={handleLikeClick} 
              style={{ color: userLiked ? 'red' : 'inherit' }} 
          />
          <CommentsPopup postId={post.PostId} commentData={post.Comments}/>                   
        </div>     
        <div className="post-footer-info">
          <div className="post-footer-left">
          <UserPopup
            title="Likes"
            data={fetchPostLikes}
            buttonComponent={<span style={{ color: '#005c98' }}>{likes.length} likes</span>}
          />
          </div>
          <div className="post-footer-right">
            {post.PostedAt}
          </div>
        </div>
      </div>        
    </div>
  );  
}
