import React from 'react';

const BlobToImage = (props) => {

  // Check if blobData is null or undefined
  if (props === null || props === undefined) {
    return null;
  }

  const { byteArray } = props;

  if (Array.isArray(byteArray)) {
    return (
      <div>
        {byteArray.map((base64String, index) => (
          <div key={index}>
            <img src={`data:image/jpeg;base64,${base64String}`} alt={`Image ${index}`} />
          </div>
        ))}
      </div>
    );
  } else {
    return (
      <img src={`data:image/jpeg;base64,${byteArray}`} alt="Image couldn't be found" />
    );
  }
};

export default BlobToImage;
