import React, { useState, useEffect, useLayoutEffect, useRef } from 'react';

const PAGE_NUMBER = 1;

const TestData = () => {
  const [data, setData] = useState([]);
  const [containerHeight, setContainerHeight] = useState(0);
  const containerRef = useRef();
  const pageSize = 10; // Number of data points to load per page
  const maxIterations = 20; // Maximum number of iterations to prevent infinite loop
  const originalWindowHeight = window.innerHeight;
  const [page, setPage] = useState(PAGE_NUMBER);
  const [loading, setLoading] = useState(false);



  // Simulated function to generate test data
  const generateTestData = (numberOfPoints) => {
    const testData = [];
    for (let i = 1; i <= numberOfPoints; i++) {
      testData.push({ id: i, name: `Data Point ${i}` });
    }
    setLoading(false);
    return testData;
  };

  useLayoutEffect(() => {
    // Measure the container height and update the state
    setTimeout(() => {
      if (containerRef.current) {
        setContainerHeight(containerRef.current.clientHeight);
      }
    }, 0);
  }, []);

  //
  //THIS ONE WORKS!!!!
  //
  useLayoutEffect(() => {
    let iterations = 0;

    if (containerRef.current.clientHeight < originalWindowHeight) {
    const initialDataIteration = () => {
      // if (containerRef.current.clientHeight > originalWindowHeight) {
      //   console.log('Broken by height');
      //   return; // Break when the container height exceeds the original window height
      // }

      if (iterations >= maxIterations) {
        console.log('Broken due to max iterations');
        return; // Break after 5 iterations (maxIterations)
      }

      const currentPage = Math.ceil(data.length / pageSize) + 1;
      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const newData = generateTestData(endIndex); // Generate test data up to the endIndex
      setData((prevData) => [...prevData, ...newData]);

      console.log(`Iteration: ${iterations}`);
      iterations++;

      requestAnimationFrame(initialDataIteration); // Continue to the next iteration
    };

    requestAnimationFrame(initialDataIteration); // Start the loop
  }
  }, [containerHeight]);




