import React, { Component, createContext } from 'react';

const LoggedInUserContext = createContext(null);

class LoggedInUserProvider extends Component {
  constructor(props) {
    super(props);

    this.state = {
      user: null,
      setUser: this.setUser,
    };
  }

  setUser = (user) => {
    this.setState({ user });
  };

  render() {
    const { children } = this.props;
    return (
      <LoggedInUserContext.Provider value={this.state}>
        {children}
      </LoggedInUserContext.Provider>
    );
  }
}

export { LoggedInUserProvider };
export default LoggedInUserContext;
