import React from 'react'
import { Link, useNavigate } from 'react-router-dom'; 
import "./sidenav.css"
import CreatePostModal from '../modals/create-post-modal';
import * as ROUTES from '../../constants/routes';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import LogoutIcon from '@mui/icons-material/Logout';
import SocialDynamoLogo from './social-dynamo-logo.png';

function SideNav() {
  const userId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
  const ipAddress = JSON.parse(sessionStorage.getItem('ipAddress'))?.ipAddress || null;
  const navigate = useNavigate();

  const handleLogout = async () =>{

    const objMap = { userId };
    const finalBody = JSON.stringify(objMap);
    
    //Fetch data
    const response = await fetch(ipAddress + '/authentication/logout', {
      method: 'PUT',
      body: finalBody,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    //Handle different responses
    const dataWrapper = await response.json();
    const { statusCode, data } = dataWrapper;

    if (statusCode !== 200) {      
      sessionStorage.clear();
      navigate(ROUTES.LOGIN)
    }
}

  return (
    <div className='sidenav'>
      <Link to={`${ROUTES.DASHBOARD}`} style={{ textDecoration: 'none' }}>
        <img className='sidenav-logo' src={SocialDynamoLogo} alt='Image Not Found'/>
      </Link>
      
      <div className="sidenav-buttons">
        <Link to={`${ROUTES.DASHBOARD}`} style={{ textDecoration: 'none' }}>
            <div className='sidenav-button'>
                <HomeIcon/>
                <span>Home</span>
            </div>
        </Link>

        <Link to={`${ROUTES.SEARCH}`} style={{ textDecoration: 'none' }}>
            <div className='sidenav-button'>
                <SearchIcon />
                <span>Search</span>
            </div>
        </Link>

        <Link to={`/p/${userId}`} style={{ textDecoration: 'none' }}>
            <div className='sidenav-button'>
                <AccountCircleIcon/>
                <span>Profile</span>
            </div>
        </Link>

        <div className='sidenav-button'>
            <CreatePostModal/>
        </div>     

        <br/>
        <br/>
        <hr style={{width: '100%'}}/>
        
        
        <div className='sidenav-button' onClick={handleLogout}>    
            <LogoutIcon/>        
            <span className='logout-button'>Logout</span>            
        </div> 
        
      </div>
    </div>    
  )
}

export default SideNav;
