import React, { useState } from "react";
import { lazy, Suspense } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ReactLoader from './components/loader';
import * as ROUTES from './constants/routes';
import UserContext from './context/user';
import useAuthListener from './hooks/use-auth-listener';

import ProtectedRoute from './util/protected-route';

const Login = lazy(() => import('./pages/login'));
const SignUp = lazy(() => import('./pages/sign-up'));
const Feed = lazy(() => import('./pages/feed'));
const Search = lazy(() => import('./pages/search'));
const Profile = lazy(() => import('./pages/profile'));
const NotFound = lazy(() => import('./pages/not-found'));

export default function App() {
  const { user } = useAuthListener();

  return (

    <UserContext.Provider value={{ user }}>
      <Router>
        <Suspense fallback={<div>Loading cities...</div>}>
          <Switch>
            <Route path={ROUTES.LOGIN} component={Login} />
            <Route path={ROUTES.SIGN_UP} component={SignUp} />
            <ProtectedRoute user={user} path={ROUTES.PROFILE} exact> 
               <Profile />
             </ProtectedRoute>
             <ProtectedRoute user={user} path={ROUTES.FEED} exact>
               <Feed />
             </ProtectedRoute>
             <ProtectedRoute user={user} path={ROUTES.SEARCH} exact> 
               <Search />
             </ProtectedRoute>
            <Route component={NotFound} />
          </Switch>
        </Suspense>
      </Router>
    </UserContext.Provider>
  );
}

