import { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import * as ROUTES from '../constants/routes';
import './login.css';

export default function Login() {
  const history = useHistory();

  const [emailAddress, setEmailAddress] = useState('');
  const [password, setPassword] = useState('');

  const [error, setError] = useState('');
  const isInvalid =
    password === '' ||
    emailAddress === '' ||
    !/^[\w.%+-]+@[\w.-]+\.[A-Za-z]{2,}$/i.test(emailAddress);

  const handleLogin = async (event) => {
    event.preventDefault();

    const objMap = { emailAddress, password };
    const finalBody = JSON.stringify(objMap);

    //Fetch data
    const response = await fetch('http://20.49.168.20:80/authentication/login', {
      method: 'PUT',
      body: finalBody,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    //Handle different responses
    const dataWrapper = await response.json();
    const { statusCode, data } = dataWrapper;

    if (statusCode === 200) {
      // Store the user in local storage
      const user = { UserId: dataWrapper.value.userId };
      sessionStorage.setItem('userId', JSON.stringify(user));
      storeUserInfo(user);

      history.push(ROUTES.DASHBOARD);
    } else {
      setEmailAddress('');
      setPassword('');
      setError(dataWrapper.value)
    }
  };

  const storeUserInfo = async (user) =>{
    //Fetch user data using userId
    const response = await fetch(`http://20.49.168.20:80/account/Profile/${user.UserId}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${document.cookie.split('; ').find(row => row.startsWith('token=')).split('=')[1]}`
      },
    });

    const data = await response.json()
    const userData = { 
      name: data.forename + ' ' + data.surname, 
      profileDescription: data.profileDescription,
      numberFollowers: data.numberOfFollowers,
      profilePicture: data.profilePicture
    };
    const userDataString = JSON.stringify(userData);
    localStorage.setItem('userData', userDataString);
  }

  useEffect(() => {
    document.title = 'Login';
  }, []);

  return (
    <div className="login-container">
      <div className="login-image-container">
        <img src="/images/login-image.jpg" alt="social media image" />
      </div>
      <div className="login-form-container">
        <div className="login-form-content">
          <h1 className="login-logo-container">
            <img src="/images/social-dynamo-logo.png" alt="Social Dynamo" className="signup-logo" />
          </h1>

          {error && <p className="login-message">{error}</p>}

          <form onSubmit={handleLogin} method="POST">
            <input
              aria-label="Enter your email address"
              type="text"
              placeholder="Email address"
              className="login-input"
              onChange={({ target }) => setEmailAddress(target.value)}
              value={emailAddress}
            />
            <input
              aria-label="Enter your password"
              type="password"
              placeholder="Password"
              className="login-input"
              onChange={({ target }) => setPassword(target.value)}
              value={password}
            />
            <button
              disabled={isInvalid}
              type="submit"
              className={`login-input-button
            ${isInvalid && 'login-input:disabled'}`}
            >
              Login
            </button>
          </form>
        </div>
        <div className="login-signup-container">
          <p className="login-signup-text">
            Don't have an account?{` `}
            <Link to={ROUTES.SIGN_UP} className="login-signup-link">
              Sign up
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
}
