import { useEffect, useState, lazy, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import ReactLoader from './components/loader';
import * as ROUTES from './constants/routes';

import ProtectedRoutes from './helpers/protected-route';

const Login = lazy(() => import('./pages/login'));
const SignUp = lazy(() => import('./pages/sign-up'));
const Dashboard = lazy(() => import('./pages/dashboard'));
const Profile = lazy(() => import('./pages/profile'));
const NotFound = lazy(() => import('./pages/not-found'));
const Search = lazy(() => import('./pages/search'));

export default function App() {
  const [user, setUser] = useState();
  const [loading, setLoading] = useState(true);
  // const navigate = useNavigate();
  const [initialRender, setInitialRender] = useState(true);

  // useEffect(() => {
  //   // Skip the effect on the initial render
  //   if (initialRender) {
  //     setInitialRender(false);
  //     return;
  //   }

  //   const userFromLocalStorage = JSON.parse(sessionStorage.getItem('userId'));

  //   if (userFromLocalStorage) {
  //     setUser(userFromLocalStorage);
  //   } else {
  //     navigate(ROUTES.LOGIN);
  //   }

  //   setLoading(false);
  // }, [initialRender, navigate]); // Removed 'history' dependency

  // // Move the conditional rendering logic outside of the useEffect hook
  // if (loading) {
  //   return <ReactLoader />;
  // }

  useEffect(() => {
    const userFromLocalStorage = JSON.parse(sessionStorage.getItem('userId'));

    if (userFromLocalStorage) {
          setUser(userFromLocalStorage);
        }
  }, []);

  return (
    <>
      <Router>
        <Suspense fallback={<ReactLoader />}>
          <Routes>
            <Route path={ROUTES.LOGIN} component={Login} />
            <Route path={ROUTES.SIGN_UP} component={SignUp} />
            <Route element={<ProtectedRoutes />}>
              <Route element={<Profile/>} path={ROUTES.LOGIN}/>
              <Route element={<Dashboard/>} path={ROUTES.DASHBOARD} exact/>
              <Route element={<Search/>} path={ROUTES.SEARCH}/>
              <Route element={<NotFound/>} path={ROUTES.NOT_FOUND} exact/>
            </Route>

            {/* <ProtectedRoute user={user} path={ROUTES.PROFILE} element={<Profile/>}/>
              <Profile />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.DASHBOARD} element={<Dashboard/>}/>
              <Dashboard />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.SEARCH} element={<Search/>}/>
              <Search />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.NOT_FOUND} element={<NotFound/>}/> 
            exact>
              <NotFound />
            </ProtectedRoute>
            <Route>
              {user ? <NotFound/> : <Login/>}
            </Route> */}
          </Routes>
        </Suspense>
      </Router>
    </> 
  );
}
