import { useEffect, useState} from 'react'
import { useNavigate } from 'react-router-dom';
import * as ROUTES from '../constants/routes';
import { Alert, AlertTitle } from '@mui/material';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';

function RefreshLogin() {
    const userId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
    const ipAddress = JSON.parse(sessionStorage.getItem('ipAddress'))?.ipAddress || null;
    const [modal, setModal] = useState(false);
    const [showAlert, setShowAlert] = useState(false);
    const navigate = useNavigate();
    
    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    const handleRefreshToken = async () =>{
        const objMap = { userId };
        const finalBody = JSON.stringify(objMap);
        
        //Fetch data
        const response = await fetch('http://20.49.168.20:80/authentication/refresh-token', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        //Handle different responses
        const dataWrapper = await response.json();
        const { statusCode, data } = dataWrapper;

        if (statusCode !== 200) {      
            sessionStorage.clear();  
            setShowAlert(true);
        }
    };

    const handleModalClose = () => {        
        navigate(ROUTES.LOGIN);
    };

    useEffect(() => {
        const fetchData = async () => {
            await handleRefreshToken();
        };

        fetchData();
    }, []);

    return (
        <>        
            {showAlert &&  (() => {
                setModal(true)
                return (
                    <div className='modal'>
                        <div onClick={handleModalClose} className="overlay"></div>
                        <div className='post-alert'>
                            <Alert severity="error">
                                <AlertTitle>Session Expired</AlertTitle>
                                Session has expired and token cannot be refreshed. You will be redirected to login.
                            </Alert>
                        </div>
                        <button className="close-modal" onClick={handleModalClose}>
                            <CancelOutlinedIcon className='closeIcon'/>
                        </button>
                    </div>
                );
            })()}
        </>
    );    
}

export default RefreshLogin
