import { useState, useEffect } from 'react'
import moment from 'moment'
import Comment from '../comment/comment';
import RefreshLogin from '../../util/refresh-login';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import SendIcon from '@mui/icons-material/Send';
import ChatBubbleOutlineIcon from "@mui/icons-material/ChatBubbleOutline";
import "./comments-popup.css"
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

export default function CommentsPopup({postId, commentData}) {
    const [combinedData, setCombinedData] = useState([]);
    const [modal, setModal] = useState(false);
    const [comment, setComment] = useState('');
    const url = JSON.parse(localStorage.getItem('url')) || null;

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }
    
    //Load initial comments data
    useEffect(async() =>{       

        const fetchData = async () => {
            const userIds = commentData.map((comment) => comment.authorId);
            
            const objMap = { userIds };
            const finalBody = JSON.stringify(objMap);

            try{
                //Send data              
                const response = await fetch(url + '/account/Profiles', {
                    method: 'PUT',
                    credentials: 'include',
                    body: finalBody,
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                const data = await response.json();

                if (response.ok){
                    if(!response.headers.get('content-type').includes('application/json')){
                        displayError("Couldn't load comments.");
                        return;
                    }
              
                    const responseJson = await response.json();

                    
                    //Map response and update
                    const userId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
                    const combined = commentData.map((item, index) => ({
                        userId: item.authorId,
                        name: data[index].forename + ' ' + data[index].surname,
                        profilePictureBlob: data[index].profilePictureBlob,
                        commentId: item.commentId,
                        comment: item.comment,
                        postedAt: item.postedAt,
                        likes: item.likes ? item.likes.length : 0,
                        hasUserLiked: item.likes && item.likes.some(like => like.userId === userId)
                    }));        
                    setCombinedData(combined);

                }else if(response.status === 401){
                    RefreshLogin();
                    fetchData();
                }else if(response.status === 400){
                    displayError(response.title);
                }else{
                    displayError("Unexpected error occurred, please try again.");
                }
            }catch(error){
                displayError("Unexpected error occurred, couldn't fetch comments.");
                console.log(error);
            }
        }
        fetchData();
    }, []);


    const handleInputChange = (e) => {
        setComment(e.target.value);
    };

    //Handle submitting comment
    const handleSubmit = async (event) => {
        event.preventDefault();

        const authorId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
        
        const objMap = { postId, authorId, comment };
        const finalBody = JSON.stringify(objMap);

        try{
            //Send data
            const responseComment = await fetch(url + '/posts/comment', {
                method: 'PUT',
                mode: 'cors',
                credentials: 'include',
                body: finalBody,
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if(responseComment.ok){
                //Call to retrieve user data      
                const responseUser = await fetch(url + `/account/Profile/${userId}`, {
                    method: 'GET',
                    mode: 'cors',
                    credentials: 'include',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
            }else if(responseComment.status === 401){
                RefreshLogin();
                handleSubmit(event);
            }else if(responseComment.status === 400){
                displayError(responseComment.title);
                return;
            }else{
                displayError("Unexpected error occurred.");
                return;
            }

            const data = await responseUser.json();

            if(responseUser.ok){

                const newData = {
                    userId: authorId,
                    name:  data.forename + ' ' + data.surname,
                    profilePictureBlob: data.profilePictureBlob,
                    comment: comment,
                    postedAt: moment().local().format('yyyy/MM/DD HH:mm'),
                    likes: 0,
                    hasUserLiked: false
                };
                setCombinedData(prevData => [...prevData, newData]);

                setComment('');
            }else if(responseComment.status === 401){
                RefreshLogin();
                handleSubmit(event);
            }else if(responseComment.status === 400){
                displayError(responseComment.title);
            }
        }catch(error){
            console.log(error);
            displayError("Unexpected error occurred, please try again.")
        }
    };

    const handleDeleteComment = async(commentId) =>{
        const objMap = { commentId };
        const finalBody = JSON.stringify(objMap);
        
        try{
          //Fetch data
          const response = await fetch(url + '/posts/deletecomment', {
            method: 'DELETE',
            mode: 'cors',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
          });
          const responseJson = await response.json();
    
          //Handle different responses
          if (response.ok) {
            setComments(comments.filter(comment => comment.commentId !== commentId));
          } else if (response.status === 401){
            RefreshLogin();
            handleDeleteComment();
          }else if (response.status === 400){            
            displayError(responseJson.title)
          }else{
            displayError("Unexpected error occurred, please try again.");
          }
        }catch(error){
          displayError("Unexpected error occurred, please try again.");
        } 
    }
 
    const displayError = (message) => {
        toast.error(message);
    }

  return (
    <>
      <ChatBubbleOutlineIcon onClick={toggleModal}/>

      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
            <div className="modal-content">
                <h1>{Comments}</h1>                               
                {combinedData.map((data, index) => {
                    return (
                        <Comment key={index} data={data} onDelete={handleDeleteComment} />
                    );
                })}

                <div className='modal-footer'>
                    <input
                        className="comment-input"
                        type="text"
                        value={comment}
                        onChange={handleInputChange}
                        placeholder='Enter a comment'
                    />                    
                    <button className="submit-modal" onClick={handleSubmit}>
                        <SendIcon className='sendIcon'/>
                    </button>
                </div>
                <button className="close-modal" onClick={toggleModal}>
                    <CancelOutlinedIcon className='closeIcon'/>
                </button>
            </div>
            <div className='toast-cont'>
                <ToastContainer position='top-center' theme='light' />
            </div>
        </div>
      )}      
    </>
  )
}
