import { useEffect, useState, lazy, Suspense } from 'react';
import { BrowserRouter as Router, Route, Switch, useHistory, useNavigate } from 'react-router-dom';
import ReactLoader from './components/loader';
import * as ROUTES from './constants/routes';

import ProtectedRoute from './helpers/protected-route';

const Login = lazy(() => import('./pages/login'));
const SignUp = lazy(() => import('./pages/sign-up'));
const Dashboard = lazy(() => import('./pages/dashboard'));
const Profile = lazy(() => import('./pages/profile'));
const NotFound = lazy(() => import('./pages/not-found'));
const Search = lazy(() => import('./pages/search'));

export default function App() {
  const [user, setUser] = useState();
  const [loading, setLoading] = useState(true);
  const history = useHistory();
  const navigate = useNavigate();
  const [initialRender, setInitialRender] = useState(true);

  useEffect(() => {

    // Skip the effect on the initial render
    if (initialRender) {
      setInitialRender(false);
      return;
    }

    const userFromLocalStorage = JSON.parse(sessionStorage.getItem('userId'));

    if (userFromLocalStorage) {
      setUser(userFromLocalStorage);
    // } else if(history){
    //   history.push(ROUTES.LOGIN);
    // }else{
    //   return;
    // }
    }else{
      use
    }

    setLoading(false);
  }, [initialRender, history]);

  if (loading) {
    return <ReactLoader />;
  }

  return (
    <>
      <Router>
        <Suspense fallback={<ReactLoader />}>
          <Switch>
            <Route path={ROUTES.LOGIN} component={Login} />
            <Route path={ROUTES.SIGN_UP} component={SignUp} />
            <ProtectedRoute user={user} path={ROUTES.PROFILE} exact>
              <Profile />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.DASHBOARD} exact>
              <Dashboard />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.SEARCH} exact>
              <Search />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.NOT_FOUND} exact>
              <NotFound />
            </ProtectedRoute>
            <Route>
              {user ? <NotFound/> : <Login/>}
            </Route>
          </Switch>
        </Suspense>
      </Router>
    </> 
  );
}
