import { lazy, Suspense, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ReactLoader from './components/loader';
import * as ROUTES from './constants/routes';
import AuthUserContext from './context/auth-user';
import useAuthListener from './hooks/use-auth-user';

import ProtectedRoute from './helpers/protected-route';

const Login = lazy(() => import('./pages/login'));
const SignUp = lazy(() => import('./pages/sign-up'));
const Dashboard = lazy(() => import('./pages/dashboard'));
const Profile = lazy(() => import('./pages/profile'));
const NotFound = lazy(() => import('./pages/not-found'));
const Search = lazy(() => import('./pages/search'));

export default function App() {
  const { user } = useAuthListener();
  const [loading, setLoading] = useState(true);
  const [parsedUser] = useState(null)
  const [userId, setUserId] = useState();

  //Need to get userId from session
  //Only get once it's loaded
  //Can check each time route is called?




  // useEffect(() => {
  //   const userFromLocalStorage = localStorage.getItem('user');

  //   if (!userFromLocalStorage === undefined) {      
  //       parsedUser = JSON.parse(userFromLocalStorage);
  //       setUser(parsedUser);
  //   }

  //   setLoading(false);
  // }, []);

  // if (loading) {
  //   return <ReactLoader />;
  // }

  // const handleLogin = (user) => {
  //   setUser(user);
  // };

  return (
    // <AuthUserContext.Provider value={ JSON.parse(sessionStorage.getItem('userId'))?.UserId || null }>
    <AuthUserContext.Provider value={{user}}>
      {console.log("In app.js router: " + user.userId)}
        <Router>
          <Suspense fallback={<ReactLoader />}>
            <Switch>
              <Route path={ROUTES.LOGIN} component={Login} />
              <Route path={ROUTES.SIGN_UP} component={SignUp} />
              <ProtectedRoute user={user} path={ROUTES.LOGIN} exact>
                <Profile />
              </ProtectedRoute>
              <ProtectedRoute user={user} path={ROUTES.SEARCH} exact>
                <Search />
              </ProtectedRoute>              
              <ProtectedRoute user={user} path={ROUTES.DASHBOARD} exact>
                <Dashboard />
              </ProtectedRoute>
              <ProtectedRoute user={user} path={ROUTES.NOT_FOUND} exact>
                <NotFound />
              </ProtectedRoute>
              {/* <Route>
                {user ? <NotFound/> : <Login/>}
              </Route> */}
            </Switch>
          </Suspense>
        </Router>

    </AuthUserContext.Provider>
  );
}
