// export default BlobToImage;

import { useState } from "react";

const BlobToImage = (props) => {
  const { byteArray } = props;
  const [largestWidth, setLargestWidth] = useState(0);
  const [largestHeight, setLargestHeight] = useState(0);
  let images = [];

  //Check if blobData is null or undefined
  if (byteArray === null || byteArray === undefined) {
    return null;
  }

  if (Array.isArray(byteArray)) {
    images = byteArray.map((byteData, index) => {
      const blob = new Blob([new Uint8Array(byteData)], { type: 'image/jpeg' });
      const imageUrl = URL.createObjectURL(blob);

      const img = new Image();
      img.src = imageUrl;

      let natWidth = 0;
      let natHeight = 0;

      img.onload = () => {
        const natWidth = img.naturalWidth;
        const natHeight = img.naturalHeight;

        if (natWidth > largestWidth) {
          setLargestWidth(natWidth);
        }

        if (natHeight > largestHeight) {
          setLargestHeight(natHeight);
        }
      };

      images.push(
        <img
          key={index}
          src={imageUrl}
          alt={`Image ${index}`}
        />
      );

      img.src = '';
      
    });
  } else {
    const blob = new Blob([new Uint8Array(byteArray)], { type: 'image/jpeg' });
    const imageUrl = URL.createObjectURL(blob);
    images.push(<img key={0} src={imageUrl} alt="Image" />);
  }  
  return { images, largestWidth, largestHeight };
}

export default BlobToImage;
