import React, { useState, useEffect, useLayoutEffect, useRef } from 'react';
import LoadingSpinner from '../components/loader';
import Post from '../components/post/post';
import SideNav from '../components/sidebar/sidenav'; 

const PAGE_NUMBER = 1;
var currPage = 1;

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [containerHeight, setContainerHeight] = useState(0);
  const containerRef = useRef();
  const originalWindowHeight = window.innerHeight;
  const [page, setPage] = useState(PAGE_NUMBER);
  const [loading, setLoading] = useState(false);
  const [resultsEnd, setResultsEnd] = useState(false);
  const ipAddress = JSON.parse(sessionStorage.getItem('ipAddress')) || null;

  useEffect(() => {
    document.title = 'Dashboard - Social Dynamo';
  }, []);

  const getPosts = async () => {

        // const userId = JSON.parse(sessionStorage.getItem('userId'))?.userId || null;
        // //Fetch data
        // const response = await fetch(ipAddress + `/account/Profile/${userId}`, {
        //   method: 'GET',
        //   mode: 'cors',
        //   credentials: 'include',
        //   headers: {
        //       'Content-Type': 'application/json',
        //   },
        // });

        // //Handle different responses
        // const dataWrapper = await response.json();
        // const { statusCode, data } = dataWrapper;

        // console.log("Status code in method of dash is: " + statusCode);


        // if (statusCode === 200) {
        //   console.log("Success")     
        // } else if (statusCode === 401){
        //   RefreshLogin();

        // } else {
        //   console.log("Failed")
        // }





    const userId = JSON.parse(sessionStorage.getItem('userId'))?.userId || null;
    const testIp = JSON.parse(sessionStorage.getItem('ipAddress')) || null;
    console.log("ID in session storage in dashboard is: " + userId);
    console.log("IP address is testtt: " + testIp);

    try{
      //Fetch data
      const postResponse = await fetch(ipAddress + `/baseaggregate/feed/${userId}/${currPage}`, {
        method: 'GET',
        mode: 'cors',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      console.log("Should've fetched");
      console.log(postResponse.text);
      const responseJson = await postResponse.json();
      console.log("Converted to JSON");
      console.log("Status: " + postResponse.status);

      //Handle different responses
      if (postResponse.ok) {

        setData((prevFiles) => [...prevFiles, responseJson.value]);
        setPage(page + 1);

      } else if (postResponse.status === 401){
        RefreshLogin();
        getPosts();
      }else if(postResponse.status === 400){

      }
      else {
        setResultsEnd(true);
      }
    }catch(error){
      console.log(error);
    }

    currPage++
  };

  useLayoutEffect(() => {
    // Measure the container height and update the state
    setTimeout(() => {
      if (containerRef.current) {
        setContainerHeight(containerRef.current.clientHeight);
      }
    }, 0);
  }, []);

  //Load data until it exceeds length of the page
  useLayoutEffect(() => {
    let iterations = 0;    
    const maxIterations = 5;

    const initialDataIteration = () => {
      if (containerRef.current.clientHeight > originalWindowHeight || resultsEnd || iterations > maxIterations) return;

      getPosts();

      iterations++;
      requestAnimationFrame(initialDataIteration); // Continue to the next iteration
    };

    requestAnimationFrame(initialDataIteration); // Start the loop
  }, [containerHeight]);

//Infinite scrolling logic
const handleScroll = () => {
  const isScrollingToBottom =
    window.innerHeight + document.documentElement.scrollTop + 1 >=
    document.documentElement.scrollHeight

  if (isScrollingToBottom && !loading && !resultsEnd) {
    setLoading(true);

    getPosts();

    setTimeout(() => {
      setLoading(false);
    }, 1500);
  }
};

useLayoutEffect(() => {
  window.addEventListener('scroll', handleScroll);
  return () => window.removeEventListener('scroll', handleScroll);
}, []);

  return (
    <div ref={containerRef}>   
      <SideNav/>
      {data.map((item, index) => {
          return (
              <Post key={index} post={item[index]} />
          );
      })}
      {loading && <LoadingSpinner />}
      {resultsEnd && "No More Posts"}
    </div>
  );
};

export default Dashboard;
