import React, {useState, useEffect} from "react";
import "./post.css";
import "./files.css";
import CommentsPopup from "../modals/comments-popup";
import UserPopup from "../modals/user-popup";
import UserProfileHeader from "../user-profile-header";
import BlobToImage from "../../util/blob-to-image";
import ImageCarousel from "./image-carousel";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";

export default function Post({ post }) {
  const [userLiked, setUserLiked] = useState(false);
  const [likes, setLikes] = useState(post.Likes);
  const [hasMultipleFiles, setHasMultipleFiles] = useState(false)
  
  const imageUrls = BlobToImage({ byteArray: post.Files });
  
  const userRow = {
    profilepictureblob: post.ProfilePicture,
    name: post.UsersName,
    userid: post.UserId
  }
  let debounceTimeout;


  useEffect(() => {
    setHasMultipleFiles(post.Files.length > 1);
  }, [post.Files.length]);

  const handleLikeClick = () => {
    setUserLiked(!userLiked);

    // Update the local state of likes
    const newLikes = userLiked ? likes - 1 : likes + 1;
    setLikes(newLikes);
    
    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }

    debounceTimeout = setTimeout(async () => {            
      const postId = post.postId;
      const likeUserId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
      const objMap = { postId, likeUserId };
      const finalBody = JSON.stringify(objMap);

      //Send data
      const response = await fetch('http://20.49.168.20:80/posts/likepost', {
        method: 'PUT',
        body: finalBody,
        headers: {
            'Content-Type': 'application/json',
        },
      });
    }, 300);
  };

  const fetchPostLikes = async () => {
    // const userIds = post.likes.map((like) => like.LikeUserId);
      
    // const objMap = { userIds };
    // const finalBody = JSON.stringify(objMap);

    // //Send data              
    // const response = await fetch('http://20.49.168.20:80/account/Profiles', {
    //   method: 'PUT',
    //   body: finalBody,
    //   headers: {
    //     'Content-Type': 'application/json',
    //   },
    // });

    // const data = await response.json();

    // //Map response and update
    // const processedData = post.likes.map((item, index) => ({
    //   userId: item.LikeUserId,
    //   name: data[index].forename + ' ' + data[index].surname,
    //   profilePictureBlob: data[index].profilePictureBlob
    // }));      
    
    // if(userLiked) processedData.unshift(userRow);

    // return processedData;
    const testLikesData = [
    {
      userId: 1,
      name: 'John Doe',
      profilePictureBlob: null
    },
    {
      userId: 2,
      name: 'Jane Smith',
      profilePictureBlob: null
    },
    {
      userId: 3,
      name: 'Bob Johnson',
      profilePictureBlob: null
    }];

    return testLikesData;
  };

  
  console.log(imageUrls);
    
  console.log("Has multiple files: " + hasMultipleFiles);
  return (
    <div className="post">
      <div className="post-header">             
        <UserProfileHeader profilepictureblob={post.ProfilePicture} name={post.UsersName} userid={post.UserId}/>           
      </div>
      <div className="post-image">


        {/*
          <div className="image" style={{ backgroundImage: `url(${imageUrls})` }}/>
        */}

        

        {hasMultipleFiles ? (
          <ImageCarousel images={imageUrls}/>
        ) : (

          //<div className="image" style={{ backgroundImage: `url(${imageUrls[0].props.src})` }}/>
          //<img className="image" src={post.Files[0].props.src} alt={`Image not found`}/>
          <img
                className="image"
                src={imageUrls[0].props.src}
                alt={`Image not found`}
            />
        
        )}
      </div>     
      <div className="post-footer">
        <div className="post-footerIcons">          
          <FavoriteBorderIcon 
              onClick={handleLikeClick} 
              style={{ color: userLiked ? 'red' : 'inherit' }} 
          />
          <CommentsPopup postId={post.PostId} commentData={post.Comments}/>                   
        </div>
        {/* <UserPopup
          title={'Likes'}
          data={fetchPostLikes}
          buttonText={<span style={{ color: 'blue' }}>{likes} likes</span>}
        /> */}
        <UserPopup
          title="Likes"
          data={fetchPostLikes}
          buttonComponent={<span style={{ color: 'blue' }}>{likes} likes</span>}
        />
      </div>        
    </div>
  );  
}
