import React from 'react';

const BlobToImage = (props) => {

  // Check if blobData is null or undefined
  if (props === null || props === undefined) {
    return null;
  }

  const { byteArray } = props;

  if (Array.isArray(byteArray) && byteArray.length > 1) {
      // Handle a collection of binary blobs
      return (
        <div>
          {byteArray.map((byteData, index) => (
            <div key={index}>
              <img src={`data:image/jpeg;base64,${btoa(String.fromCharCode(...byteData))}`} alt={`Image ${index}`} />
            </div>
          ))}
        </div>
      );
    } else {
      // Handle a single binary blob
      return(
      <img src={`data:image/jpeg;base64,${btoa(String.fromCharCode(...byteArray))}`} alt="Image couldn't be found" />
    );
  }
}

export default BlobToImage;
