// export default BlobToImage;

const BlobToImage = (props) => {
  const { byteArray } = props;
  let images = [];

  // Check if byteArray is null or undefined
  if (byteArray === null || byteArray === undefined) {
    return null;
  }

  if (Array.isArray(byteArray)) {
    images = byteArray.map((byteData, index) => {
      const blob = new Blob([new Uint8Array(byteData)], { type: 'image/jpeg' });
      const imageUrl = URL.createObjectURL(blob);

      // Use an Image object to get the natural height
      const img = new Image();
      img.src = imageUrl;

      return {
        original: imageUrl,
        thumbnail: imageUrl,
        height: img.naturalHeight, // Get the natural height of the image
      };
    });
  } else {
    const blob = new Blob([new Uint8Array(byteArray)], { type: 'image/jpeg' });
    const imageUrl = URL.createObjectURL(blob);

    // Use an Image object to get the natural height
    const img = new Image();
    img.src = imageUrl;

    images.push({
      original: imageUrl,
      thumbnail: imageUrl,
      height: img.naturalHeight, // Get the natural height of the image
    });
  }

  return images;
};

export default BlobToImage;
