import React, { useState } from 'react';
import './create-post-modal.css';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import { Alert, AlertTitle } from '@mui/material';
import FileUpload from '../file-upload';

export default function CreatePostModal() {  
    const [modal, setModal] = useState(false);
    const [error, setError] = useState('');

    const [uploadedFiles, setUploadedFiles] = useState([]);
    const [caption, setCaption] = useState('');
    const [hashtag, setHashtag] = useState('');
    const fileLimit = 4;
    const [isPublishing, setIsPublishing] = useState(false);
    const url = JSON.parse(localStorage.getItem('url')) || null;

    const [showAlert, setShowAlert] = useState(false);

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }
  
    //Handle file change in FileUpload component
    const handleFilesChange = (newFiles) => {
        setUploadedFiles(() => {
            const updatedFiles = [...newFiles];

            return updatedFiles;
        });
    };

    //Handle publish post
    const handlePublishPost = async () => {
        
        if (uploadedFiles.length <= 0 && caption.trim() === '') {
            setError("Must have a caption and files added");
            return;
        }

        setIsPublishing(true);

        const authorId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
        const formFiles = new FormData();

        files.forEach((file, index) =>{
            formFiles.append(file);
        });

        const objMap = { authorId, hashtag, caption, formFiles };
        const finalBody = JSON.stringify(objMap);
    
        try{
            //Fetch data
            const response = await fetch(url + '/baseaggregate/post', {
                method: 'PUT',
                mode: 'cors',
                credentials: 'include',
                body: finalBody,
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            const responseJson = await response.json();

            //Handle different responses
            if (statusCode === 200) {
                setUploadedFiles([]);
                setCaption('');
                setHashtag('');
                setError('');
                toggleModal();

                setIsPublishing(false);

                setShowAlert(true);
                setTimeout(() => {
                    setShowAlert(false);
                }, 5000);

            } else if (statusCode === 401){
                RefreshLogin();
                handlePublishPost();
            }else if (statusCode === 400){
                setError(responseJson.value);
                setIsPublishing(false);
            }
        }catch(error){
            setError("Unexpected error occurred, please try again.");
        }
    };
    
    //Handle cancel post
    const handleCancelPost = () => {
        setUploadedFiles([]);
        setCaption('');
        setHashtag('');
        setError('');
        toggleModal();
    };

    //Render
    return( 
        <>
        <div onClick={toggleModal} className="create-icon">
            <AddCircleOutlineIcon />
            <span>Create</span>
        </div>

        {showAlert && (
            <div className='post-alert'>
                <Alert severity="success">
                    <AlertTitle>Post Published</AlertTitle>
                    Post published successfully!
                </Alert>
            </div>
        )}

        {modal && (
            <div className='modal'>
                <div onClick={toggleModal} className="overlay"></div>
                <div className='modal-body'>
                    <h1>Create Post</h1>
                    <div className='post-details'>
                        <textarea
                            placeholder="Post caption or description"
                            className='caption-textarea'
                            rows='3'
                            onChange={({ target }) => setCaption(target.value)}
                            value={caption}
                        />
                        <input
                            type="text"
                            placeholder="Hashtag"
                            className="hashtag-input"
                            pattern='/#[a-z0-9]+/'
                            onChange={({ target }) => setHashtag(target.value.replace(/\s/g, ''))}
                            value={hashtag}
                        />
                    </div>
                    <div className="modal-upload">
                        <FileUpload fileLimit={fileLimit} onFilesChange={handleFilesChange} setError={setError} />
                    </div>
                    <div className='modal-footer'>
                        {error && <p className="error-message">{error}</p>}
                        <button className="cancel-post" onClick={handleCancelPost} disabled={isPublishing}>
                            Cancel
                        </button>
                        <button className="publish-post" onClick={handlePublishPost} disabled={isPublishing}>
                            Post
                        </button>                        
                    </div>
                        
                    <button className="close-modal" onClick={toggleModal} disabled={isPublishing}>
                        <CancelOutlinedIcon className='closeIcon'/>
                    </button>
                </div>                
            </div>
        )}
    </>)   
}
