import { useEffect, useState, lazy, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import ReactLoader from './components/loader';
import * as ROUTES from './constants/routes';

import ProtectedRoute from './helpers/protected-route';

export default function App() {
  const [user, setUser] = useState();
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const [initialRender, setInitialRender] = useState(true);

  useEffect(() => {
    // Skip the effect on the initial render
    if (initialRender) {
      setInitialRender(false);
      return;
    }

    const userFromLocalStorage = JSON.parse(sessionStorage.getItem('userId'));

    if (userFromLocalStorage) {
      setUser(userFromLocalStorage);
    } else {
      navigate(ROUTES.LOGIN);
    }

    setLoading(false);
  }, [initialRender, navigate]); // Removed 'history' dependency

  // Move the conditional rendering logic outside of the useEffect hook
  if (loading) {
    return <ReactLoader />;
  }

  return (
    <>
      {/* <Router>
        <Suspense fallback={<ReactLoader />}> */}
          <Routes>
            <Route path={ROUTES.LOGIN} component={Login} />
            <Route path={ROUTES.SIGN_UP} component={SignUp} />
            <ProtectedRoute user={user} path={ROUTES.PROFILE} exact>
              <Profile />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.DASHBOARD} exact>
              <Dashboard />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.SEARCH} exact>
              <Search />
            </ProtectedRoute>
            <ProtectedRoute user={user} path={ROUTES.NOT_FOUND} exact>
              <NotFound />
            </ProtectedRoute>
            <Route>
              {user ? <NotFound/> : <Login/>}
            </Route>
          </Routes>
        {/* </Suspense>
      </Router> */}
    </> 
  );
}
