import React, { useState, useEffect } from 'react';
import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
import './image-carousel.css';
import './files.css';

const ImageCarousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [largestWidth, setLargestWidth] = useState(0);
  const [largestHeight, setLargestHeight] = useState(0);

  useEffect(() => {
    const imagePromises = images.map(img => {
      return new Promise((resolve) => {
        const image = new Image();
        image.src = img.props.src;
        image.onload = () => {
          resolve({
            width: image.naturalWidth,
            height: image.naturalHeight
          });
        };
      });
    });

    Promise.all(imagePromises).then((dimensions) => {
      const widths = dimensions.map(d => d.width);
      const heights = dimensions.map(d => d.height);
      setLargestWidth(Math.max(...widths));
      setLargestHeight(Math.max(...heights));
    });
  }, [images]);

  const goToPrevious = () => {
    const isFirstImage = currentIndex === 0;
    const newIndex = isFirstImage ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  const goToNext = () => {
    const isLastImage = currentIndex === images.length - 1;
    const newIndex = isLastImage ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const goToSlide = (slideIndex) => {
    setCurrentIndex(slideIndex);
  };

  console.log("Largest height: " + largestHeight);
  console.log("Largest width: " + largestWidth);

  const arrowStyle = {
    position: 'absolute',
    top: '50%',
    transform: 'translateY(-50%)',
    fontSize: '45px',
    color: '#fff',
    zIndex: '1',
    cursor: 'pointer'
  };

  const arrowLeftPosition = {
    ...arrowStyle,
    left: 'calc(0px - 45px)' // Adjust the 45px based on your design
  };

  const arrowRightPosition = {
    ...arrowStyle,
    right: 'calc(0px - 45px)' // Adjust the 45px based on your design
  };

  return (
    <div className="slider">
      <div>
        {/* <ArrowCircleLeftIcon onClick={goToPrevious} className="left-arrow" />
        <ArrowCircleRightIcon onClick={goToNext} className="right-arrow" /> */}
        <ArrowCircleLeftIcon
          onClick={goToPrevious}
          className="left-arrow"
          style={arrowLeftPosition}
        />
        <ArrowCircleRightIcon
          onClick={goToNext}
          className="right-arrow"
          style={arrowRightPosition}
        />
      </div>
      <div className='post-image' style={{ width: largestWidth, height: largestHeight }}>
        <img
          className="image"
          src={images[currentIndex].props.src}
          alt={`Image ${currentIndex}`}
        />
      </div>
      <div className="dots-container">
        {images.map((image, imageIndex) => (
          <div
            className="dot"
            key={imageIndex}
            onClick={() => goToSlide(imageIndex)}
          >
            ‚óè
          </div>
        ))}
      </div>
    </div>
  );
}

export default ImageCarousel;
