import React, {useState, useEffect } from 'react'
import { useUser } from '../../hooks/use-user';
import Comment from '../post/comment';
import CancelOutlinedIcon from '@mui/icons-material/CancelOutlined';
import SendIcon from '@mui/icons-material/Send';
import "./user-popup.css"

export default function CommentsPopup({title, postId, commentData}) {
    const [combinedData, setCombinedData] = useState([]);
    const [modal, setModal] = useState(false);
    const [comment, setComment] = useState({
        comment: '',
        postedAt: '1900/01/01',
        likes: 0
    });

    //Modal logic
    const toggleModal = () => {
        setModal(!modal)
    }

    if(modal) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    //Load initial comments data
    useEffect(async() =>{        
        const userIds = commentData.map((comment) => comment.authorId);
        
        const objMap = { userIds };
        const finalBody = JSON.stringify(objMap);

        //Send data
        //async function fetchData(){        
            // const response = await fetch('http://20.49.168.20:80/account/Profiles', {
            //     method: 'PUT',
            //     body: finalBody,
            //     headers: {
            //         'Content-Type': 'application/json',
            //     },
            // });

            // const data = await response.json();

            const data = [
                {
                    profilePictureBlob: null,
                    forename: 'User 1',
                    surname: 'User1Surname',
                    userid: 'user1',
                },
                
                {
                    profilePictureBlob: null,
                    forename: 'User 2',
                    surname: 'User2Surname',
                    userid: 'user2',
                },
                
                {
                    profilePictureBlob: null,
                    forename: 'User 3',
                    surname: 'User3Surname',
                    userid: 'user3',
                },
                
                {
                    profilePictureBlob: null,
                    forename: 'User 4',
                    surname: 'User4Surname',
                    userid: 'user4',
                },
                
                {
                    profilePictureBlob: null,
                    forename: 'User 5',
                    surname: 'User5Surname',
                    userid: 'user5',
                }
            ];

            //Combine user profile data with comment data
            const userLiked = comment.Likes && comment.Likes.some(like => like.userId === userIdToCheck);
            const combined = commentData.map((item, index) => ({
                userid: item.authorId,
                name: data[index].forename + ' ' + data[index].surname,
                profilePictureBlob: data[index].profilePictureBlob,
                commentId: item.commentId,
                comment: item.comment,
                postedAt: item.postedAt,
                likes: item.Likes ? item.Likes.length : 0,
                hasUserLiked: userLiked
            }));
            
            setCombinedData(combined);
        //}
    })


    const handleInputChange = (e) => {
        setComment(e.target.value);
    };

    //Handle submitting comment
    const handleSubmit = async (event) => {
        event.preventDefault();

        

        console.log("Called API method")

        // const storedUser = localStorage.getItem('user');
        // setAuthorId = JSON.parse(storedUser);

        // const objMap = { postId, authorId, comment };
        // const finalBody = JSON.stringify(objMap);

        // //Send data
        // const response = await fetch('http://20.49.168.20:80/posts/comment', {
        //     method: 'PUT',
        //     body: finalBody,
        //     headers: {
        //         'Content-Type': 'application/json',
        //     },
        // });

        //Update data displayed
        const { userId, name, profilePicture } = useContext(useUser);
        const newData = {
            userid: userId,
            name: name,
            profilePictureBlob: profilePicture,
            comment: item.commentText,
            postedAt: item.postedAt,
            likes: 0,
            hasUserLiked: false
        };
       
        setCombinedData(prevData => [...prevData, ...newData]);
      };
 
  return (
    <>
      <button onClick={toggleModal} className="btn-modal">
        Open
      </button>

      {modal && (
        <div className="modal">
          <div onClick={toggleModal} className="overlay"></div>
            <div className="modal-content">
                <h1>{title}</h1>                               
                {combinedData.map((data, index) => {
                    return (
                        <Comment key={index} data={data} />
                    );
                })}

                <div className='modal-footer'>
                    <input
                        type="text"
                        value={comment}
                        onChange={handleInputChange}
                        placeholder="Enter a comment"
                    />                    
                    <button className="submit-modal" onClick={handleSubmit}>
                        <SendIcon className='sendIcon'/>
                    </button>
                </div>
                <button className="close-modal" onClick={toggleModal}>
                    <CancelOutlinedIcon className='closeIcon'/>
                </button>
            </div>
        </div>
      )}      
    </>
  )
}
