import React, { useState, useEffect } from 'react';
import Post from '../components/post/post';
import UserHeader from '../components/user-header';
import SideNav from '../components/sidebar/sidenav';
import RefreshLogin from '../util/refresh-login';
import "./search.css"
import SearchIcon from '@mui/icons-material/Search';

const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState({ users: [], posts: [] });
  const ipAddress = JSON.parse(sessionStorage.getItem('ipAddress'))?.ipAddress || null;

  useEffect(() => {
    document.title = 'Search - Social Dynamo';
  }, []);

  const handleSearch = async () => {
    
    await searchApiCall();
    
    //Clear search bar
    setSearchTerm('');
  };

  const searchApiCall = async () => {
    try {
      //Send data              
      const response = await fetch(ipAddress + `/search/${searchTerm}`, {
        method: 'GET',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      if(response.ok){
        const users = [];
        const posts = [];
  
        data.forEach(item => {
          if ('userId' in item) {
            users.push(item);
          } else if ('postId' in item) {
            posts.push(item);
          }
        });
  
        setSearchResults({
          users,
          posts
        });

      }else if(response.status === 401){
        RefreshLogin();
        searchApiCall();
      }
      
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className='search'>
      <SideNav/>
      
      <h1>Search</h1>
      <div className='search-top'>
        <input
          className='search-bar'
          type="text"
          placeholder="Search for a user or hashtag"
          value={searchTerm}
          onChange={({target}) => setSearchTerm(target.value)}
          onKeyDown={handleKeyPress}
        />
        <SearchIcon className={"search-icon"} onClick={handleSearch}/>
      </div>
    
      <div className='results'>
        <div className='user-results'>
          {/* Map data array to UserProfileHeader components */}
          {searchResults.users.map((userData, index) => {
            return (
              <div key={index}>
                <UserHeader                            
                  profilepictureblob={userData.ProfilePictureBlob}
                  name={userData.Forename + ' ' + userData.Surname}
                  userid={userData.UserId}
                />
              </div>
            );
          })}
          {searchResults.users.length > 0 && <div className='bottom-border'></div>}
        </div>
        <div className='post-results'>
          {/* Map data array to Post component */}
          {searchResults.posts.map((postData, index) => {
            return (
              <div key={index}>
                <Post                            
                  postId={postData.PostId}
                  userId={postData.UserId}
                  usersName={postData.UsersName}
                  profilePicture={postData.ProfilePicture}
                  hashtag={postData.Hashtag}
                  caption={postData.Caption}
                  postedAt={postData.PostedAt}
                  mediaItemIds={postData.MediaItemIds}
                  files={postData.Files}
                  likes={postData.Likes}
                  comments={postData.Comments}
                />
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Search;
