import React, { useState } from 'react';
import UserHeader from '../user-header';
import RefreshLogin from '../../util/refresh-login';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

const Comment = ({ data, onDelete }) => {
  const [liked, setLiked] = useState(data.hasUserLiked);
  const [likes, setLikes] = useState(data.likes.length);
  const loggedInUserId = JSON.parse(localStorage.getItem('userId'))?.UserId || null;
  const isLoggedInUserProfile = post.userId === loggedInUserId ? true : false;
  const url = JSON.parse(localStorage.getItem('url')) || null;
  let debounceTimeout;

  const handleLikeClick = async () => {
    setLiked(!liked);
    setLikes(prevLikes => prevLikes + (liked ? -1 : 1));

    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }

    debounceTimeout = setTimeout(async () => {            
        //Call API to handle liking/unliking. Delay accounts for accidental clicks 
        const CommentId = data.commentId;
        const LikeUserId = loggedInUserId;
        const objMap = { CommentId, LikeUserId };
        const finalBody = JSON.stringify(objMap);

        try{
          //Send data
          const response = await fetch(url + '/posts/likecomment', {
              method: 'PUT',
              mode: 'cors',
              credentials: 'include',
              body: finalBody,
              headers: {
                  'Content-Type': 'application/json',
              },
          });

          const responseData = await response.json();

          if(response.status === 401){
            RefreshLogin();
            handleLikeClick();
          }else if(response.status === 400){
            displayError(responseData.title);
          }
        }catch(error){
          console.log(error);
          displayError("Unexpected error occurred, please try again.")
        }
    }, 300);    
};

const handleDeleteComment = async () => {
  await onDelete(data.commentId);
}

const ConfirmDelete = ({closeToast}) => {
  <div>
    Are you sure you want to delete?
    <button className='delete-yes' onClick={handleDeleteComment}>Yes</button>
    <button className='delete-no' onClick={closeToast}>No</button>
  </div>
}

const displayError = (message) => {
  toast.error(message);
}

  return (
    <div>
        <div className='comment-profile-header'>
            <UserHeader
                profilepictureblob={data.profilePictureBlob}
                name={data.name}
                userid={data.userid}
            />
        </div>
      <div className='comment-body'>{data.comment}</div>
      <div className='comment-footer'>
        <div className='footer-left'>
            {likes} likes {data.postedAt}  
        </div>
        <div className='footer-right'>
            <FavoriteBorderIcon 
                onClick={handleLikeClick} 
                style={{ color: liked ? 'red' : 'inherit' }} 
            />
            {isLoggedInUserProfile && (  
                <div className='delete-button' onClick={() => toast(<ConfirmDelete />)}>                      
                    <span>Delete</span>
                </div>
            )}
        </div>
      </div>
      <div className='toast-cont'>
        <ToastContainer position='top-center' theme='light' />
      </div>
    </div>
  );
}

export default Comment;
