import React, { useState } from 'react';
import UserHeader from '../user-header';
import RefreshLogin from '../../util/refresh-login';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';

const Comment = ({ data }) => {
  const [liked, setLiked] = useState(data.hasUserLiked);
  const [likes, setLikes] = useState(data.likes.length);
  const loggedInUserId = JSON.parse(sessionStorage.getItem('userId'))?.UserId || null;
  const ipAddress = JSON.parse(sessionStorage.getItem('ipAddress'))?.ipAddress || null;
  let debounceTimeout;

  const handleLikeClick = async () => {
    setLiked(!liked);
    setLikes(prevLikes => prevLikes + (liked ? -1 : 1));

    if (debounceTimeout) {
      clearTimeout(debounceTimeout);
    }

    debounceTimeout = setTimeout(async () => {            
        //Call API to handle liking/unliking. Delay accounts for accidental clicks 
        const CommentId = data.commentId;
        const LikeUserId = loggedInUserId;
        const objMap = { CommentId, LikeUserId };
        const finalBody = JSON.stringify(objMap);

        //Send data
        const response = await fetch(ipAddress + '/posts/likecomment', {
            method: 'PUT',
            credentials: 'include',
            body: finalBody,
            headers: {
                'Content-Type': 'application/json',
            },
        });

        if(response.status === 401){
          RefreshLogin();
          handleLikeClick();
        }
    }, 300);
};

  return (
    <div>
        <div className='comment-profile-header'>
            <UserHeader
                profilepictureblob={data.profilePictureBlob}
                name={data.name}
                userid={data.userid}
            />
        </div>
      <div className='comment-body'>{data.comment}</div>
      <div className='comment-footer'>
        <div className='footer-left'>
            {likes} likes {data.postedAt}  
        </div>
        <div className='footer-right'>
            <FavoriteBorderIcon 
                onClick={handleLikeClick} 
                style={{ color: liked ? 'red' : 'inherit' }} 
            />
        </div>
      </div>
    </div>
  );
}

export default Comment;
