import React, { useState, useEffect } from 'react';
import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
import './image-carousel.css';
import './files.css';

const ImageCarousel = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [largestWidth, setLargestWidth] = useState(0);
  const [largestHeight, setLargestHeight] = useState(0);

  useEffect(() => {
    const imagePromises = images.map(img => {
      return new Promise((resolve) => {
        const image = new Image();
        image.src = img.props.src;
        image.onload = () => {
          resolve({
            width: image.naturalWidth,
            height: image.naturalHeight
          });
        };
      });
    });

    Promise.all(imagePromises).then((dimensions) => {
      const widths = dimensions.map(d => d.width);
      const heights = dimensions.map(d => d.height);
      setLargestWidth(Math.max(...widths));
      setLargestHeight(Math.max(...heights));
    });
  }, [images]);

  const goToPrevious = () => {
    const isFirstImage = currentIndex === 0;
    const newIndex = isFirstImage ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };

  const goToNext = () => {
    const isLastImage = currentIndex === images.length - 1;
    const newIndex = isLastImage ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };

  const goToSlide = (slideIndex) => {
    setCurrentIndex(slideIndex);
  };

  console.log("Largest height: " + largestHeight);
  console.log("Largest width: " + largestWidth);

